.dark {
  --button-outline: rgba(255,255,255, .10);
  --badge-outline: rgba(255,255,255, .05);

  --opaque-button-border-intensity: 9;  /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(255,255,255, .04);
  --elevate-2: rgba(255,255,255, .09);

  --background: 210 15% 8%;

  --foreground: 210 5% 95%;

  --border: 210 10% 20%;

  --card: 210 12% 12%;

  --card-foreground: 210 5% 95%;

  --card-border: 210 10% 18%;

  --sidebar: 210 14% 10%;

  --sidebar-foreground: 210 5% 95%;

  --sidebar-border: 210 12% 16%;

  --sidebar-primary: 210 85% 55%;

  --sidebar-primary-foreground: 210 5% 95%;

  --sidebar-accent: 210 12% 16%;

  --sidebar-accent-foreground: 210 5% 95%;

  --sidebar-ring: 210 85% 55%;

  --popover: 210 12% 14%;

  --popover-foreground: 210 5% 95%;

  --popover-border: 210 10% 22%;

  --primary: 210 100% 65%;

  --primary-foreground: 210 5% 95%;

  --secondary: 210 10% 16%;

  --secondary-foreground: 210 5% 95%;

  --muted: 210 8% 14%;

  --muted-foreground: 210 5% 72%;

  --accent: 210 10% 12%;

  --accent-foreground: 210 5% 95%;

  /* Enhanced semantic colors for dark mode */
  --success: 142 71% 45%;
  --success-foreground: 142 84% 2%;
  --warning: 38 92% 55%;
  --warning-foreground: 20 14% 4%;
  --info: 210 100% 65%;
  --info-foreground: 210 5% 95%;

  --destructive: 0 75% 55%;

  --destructive-foreground: 210 5% 95%;

  --input: 210 12% 25%;
  --ring: 210 85% 55%;
  --chart-1: 210 85% 65%;
  --chart-2: 120 75% 55%;
  --chart-3: 45 90% 65%;
  --chart-4: 280 75% 65%;
  --chart-5: 15 85% 65%;

  --shadow-2xs: 0px 2px 0px 0px hsl(210 10% 5% / 0.00);
  --shadow-xs: 0px 2px 0px 0px hsl(210 10% 5% / 0.00);
  --shadow-sm: 0px 2px 0px 0px hsl(210 10% 5% / 0.00), 0px 1px 2px -1px hsl(210 10% 5% / 0.00);
  --shadow: 0px 2px 0px 0px hsl(210 10% 5% / 0.00), 0px 1px 2px -1px hsl(210 10% 5% / 0.00);
  --shadow-md: 0px 2px 0px 0px hsl(210 10% 5% / 0.00), 0px 2px 4px -1px hsl(210 10% 5% / 0.00);
  --shadow-lg: 0px 2px 0px 0px hsl(210 10% 5% / 0.00), 0px 4px 6px -1px hsl(210 10% 5% / 0.00);
  --shadow-xl: 0px 2px 0px 0px hsl(210 10% 5% / 0.00), 0px 8px 10px -1px hsl(210 10% 5% / 0.00);
  --shadow-2xl: 0px 2px 0px 0px hsl(210 10% 5% / 0.00);

}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/**
 * Using the elevate system.
 * Automatic contrast adjustment.
 *
 * <element className="hover-elevate" />
 * <element className="active-elevate-2" />
 *
 * // Using the tailwind utility when a data attribute is "on"
 * <element className="toggle-elevate data-[state=on]:toggle-elevated" />
 * // Or manually controlling the toggle state
 * <element className="toggle-elevate toggle-elevated" />
 *
 * Elevation systems have to handle many states.
 * - not-hovered, vs. hovered vs. active  (three mutually exclusive states)
 * - toggled or not
 * - focused or not (this is not handled with these utilities)
 *
 * Even without handling focused or not, this is six possible combinations that
 * need to be distinguished from eachother visually.
 */
@layer base {
  /* Enhanced modern typography system */
  .type-display {
    @apply text-3xl md:text-4xl lg:text-5xl xl:text-6xl font-bold;
    letter-spacing: var(--letter-spacing-tight);
    line-height: var(--line-height-tight);
    font-feature-settings: "ss01", "ss02";
  }

  .type-hero {
    @apply text-2xl md:text-3xl lg:text-4xl font-bold;
    letter-spacing: var(--letter-spacing-tight);
    line-height: var(--line-height-snug);
  }

  .type-title {
    @apply text-xl md:text-2xl lg:text-3xl font-semibold;
    letter-spacing: var(--letter-spacing-tight);
    line-height: var(--line-height-snug);
  }

  .type-heading {
    @apply text-lg md:text-xl font-semibold;
    letter-spacing: var(--letter-spacing-normal);
    line-height: var(--line-height-snug);
  }

  .type-subtitle {
    @apply text-base md:text-lg font-medium;
    letter-spacing: var(--letter-spacing-normal);
    line-height: var(--line-height-normal);
  }

  .type-body {
    @apply text-sm md:text-base;
    line-height: var(--line-height-relaxed);
    letter-spacing: var(--letter-spacing-normal);
  }

  .type-body-large {
    @apply text-base md:text-lg;
    line-height: var(--line-height-relaxed);
    letter-spacing: var(--letter-spacing-normal);
  }

  .type-caption {
    @apply text-xs md:text-sm text-muted-foreground font-medium;
    letter-spacing: var(--letter-spacing-wide);
  }

  .type-overline {
    @apply text-xs font-semibold uppercase tracking-wider text-muted-foreground;
    letter-spacing: 0.1em;
  }

  /* Content-specific typography */
  .type-content {
    @apply font-serif text-base md:text-lg;
    line-height: var(--line-height-loose);
    letter-spacing: var(--letter-spacing-normal);
  }

  .type-content h1 {
    @apply type-hero mb-6 mt-8 first:mt-0;
  }

  .type-content h2 {
    @apply type-title mb-4 mt-8;
  }

  .type-content h3 {
    @apply type-heading mb-3 mt-6;
  }

  .type-content p {
    @apply mb-4 last:mb-0;
  }

  .type-content blockquote {
    @apply border-l-4 border-primary/20 pl-4 my-6 italic text-muted-foreground;
  }

  /* Numerical data styling */
  .num {
    font-variant-numeric: tabular-nums;
    font-feature-settings: "tnum";
  }
}

@layer utilities {

  /* Mobile safe area support */
  .safe-top {
    padding-top: env(safe-area-inset-top);
  }

  .safe-bottom {
    padding-bottom: env(safe-area-inset-bottom);
  }

  .safe-left {
    padding-left: env(safe-area-inset-left);
  }

  .safe-right {
    padding-right: env(safe-area-inset-right);
  }

  /* Mobile-first responsive design utilities */
  .mobile-full-height {
    height: 100dvh; /* Use dynamic viewport height for better mobile support */
  }

  .mobile-touch-target {
    min-height: 44px; /* iOS minimum touch target size */
    min-width: 44px;
  }

  /* Motion and interactions */
  .interactive {
    @apply transition-[colors,opacity,transform] duration-150 ease-out;
  }

  .interactive-slow {
    @apply transition-[colors,opacity,transform] duration-200 ease-out;
  }

  /* Respect reduced motion preference */
  @media (prefers-reduced-motion: reduce) {
    .interactive,
    .interactive-slow {
      @apply transition-none;
    }
  }

  /* Enhanced focus states */
  .focus-ring {
    @apply focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2;
  }

  /* Hide ugly search cancel button in Chrome until we can style it properly */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  /* .no-default-hover-elevate/no-default-active-elevate is an escape hatch so consumers of
   * buttons/badges can remove the automatic brightness adjustment on interactions
   * and program their own. */
  .no-default-hover-elevate {}

  .no-default-active-elevate {}


  /**
   * Toggleable backgrounds go behind the content. Hoverable/active goes on top.
   * This way they can stack/compound. Both will overlap the parent's borders!
   * So borders will be automatically adjusted both on toggle, and hover/active,
   * and they will be compounded.
   */
  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: -1;
    /* sits behind content but above backdrop */
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.toggle-elevate::before {
    inset: -1px;
  }

  /* Does not work on elements with overflow:hidden! */
  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: 999;
    /* sits in front of content */
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }
}

