                  key={type.id} 
                  className="hover-elevate cursor-pointer"
                  onClick={() => {
                    onAddSection?.(type.id);
                    console.log(`Add section template: ${type.id}`);
                  }}
                  data-testid={`template-${type.id}`}
                >
                  <CardContent className="p-3 md:p-4 text-center">
                    <IconComponent className="w-8 h-8 mx-auto mb-2 text-primary" />
                    <h4 className="font-medium mb-1">{type.name}</h4>
                    <p className="text-xs text-muted-foreground">{type.description}</p>
                  </CardContent>
                </Card>
              );
            })}
          </div>
        </CardContent>
      </Card>

      {/* Current Layout Sections */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Layout className="w-5 h-5" />
            Page Sections
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-3">
            {currentSections.map((section, index) => {
              const SectionIcon = getSectionIcon(section.type);
              return (
                <Card
                  key={section.id}
                  className={`hover-elevate cursor-move ${draggedItem === section.id ? 'opacity-50' : ''}`}
                  draggable
                  onDragStart={(e) => handleDragStart(e, section.id)}
                  onDragOver={handleDragOver}
                  onDrop={(e) => handleDrop(e, section.id)}
                  data-testid={`section-${section.id}`}
                >
                  <CardContent className="p-3 md:p-4">
                    <div className="flex items-center gap-4">
                      <GripVertical className="w-5 h-5 text-muted-foreground cursor-grab" />

                      <div className="flex items-center gap-3 flex-1">
                        <SectionIcon className="w-5 h-5 text-primary" />

                        <div className="flex-1">
                          <div className="flex items-center gap-2 mb-1">
                            <h4 className="font-medium">{section.title}</h4>
                            <Badge 
                              variant="secondary" 
                              className={`w-2 h-2 p-0 rounded-full ${getStatusColor(section.status)}`}
                            />
                            <span className="text-xs text-muted-foreground capitalize">
                              {section.status}
                            </span>
                          </div>
                          <p className="text-sm text-muted-foreground">{section.content}</p>
                        </div>
                      </div>

                      <div className="flex items-center gap-1">
                        <Button
                          variant="ghost"
                          size="icon"
                          className="w-8 h-8"
                          onClick={() => console.log(`Preview section: ${section.id}`)}
                          data-testid={`button-preview-${section.id}`}
                        >
                          <Eye className="w-4 h-4" />
                        </Button>

                        <Button
                          variant="ghost"
                          size="icon"
                          className="w-8 h-8"
                          onClick={() => {
                            onEditSection?.(section.id);
                            console.log(`Edit section: ${section.id}`);
                          }}
                          data-testid={`button-edit-${section.id}`}
                        >
                          <Edit3 className="w-4 h-4" />
                        </Button>

                        <Button
                          variant="ghost"
                          size="icon" 
                          className="w-8 h-8"
                          onClick={() => console.log(`Duplicate section: ${section.id}`)}
                          data-testid={`button-duplicate-${section.id}`}
                        >
                          <Copy className="w-4 h-4" />
                        </Button>

                        <Button
                          variant="ghost"
                          size="icon"
                          className="w-8 h-8 text-destructive hover:text-destructive"
                          onClick={() => console.log(`Delete section: ${section.id}`)}
                          data-testid={`button-delete-${section.id}`}
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              );
            })}
          </div>

          {currentSections.length === 0 && (
            <div className="text-center py-8">
              <Layout className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
              <h3 className="text-lg font-medium mb-2">No sections yet</h3>
              <p className="text-muted-foreground mb-4">
                Start building your page by adding sections above
              </p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/MediaLibrary.tsx.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { 
  Search, 
  Upload, 
  Grid3x3, 
  List, 
  Filter,
  Image as ImageIcon,
  Video,
  FileText,
  Download,
  Trash2,
  MoreHorizontal
} from "lucide-react";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useMedia, useDeleteMedia } from "@/hooks/useMedia";
import { useToast } from "@/hooks/use-toast";
import { LoadingCard } from "@/components/ui/loading";
import { queryClient } from "@/lib/queryClient";

interface MediaLibraryProps {
  onSelectFile?: (file: any) => void;
}

export default function MediaLibrary({ onSelectFile }: MediaLibraryProps) {
  const [searchQuery, setSearchQuery] = useState("");
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid");
  const [typeFilter, setTypeFilter] = useState("all");

  const [, setLocation] = useLocation();
  const { toast } = useToast();

  // Fetch real media data
  const { data: mediaData, isLoading, error } = useMedia(50, 0);
  const deleteMutation = useDeleteMedia();

  // Transform API data to match component expectations
  const mediaFiles = mediaData?.map((media: any) => ({
    id: media.id,
    name: media.filename,
    type: media.mimeType.startsWith('image/') ? 'image' : 
          media.mimeType.startsWith('video/') ? 'video' : 'document',
    size: formatFileSize(media.size),
    uploadDate: media.createdAt, // Keep as ISO string
    dimensions: null, // Could extract from metadata
    thumbnail: media.mimeType.startsWith('image/') ? media.url : null,
    used: false // Could track usage
  })) || [];

  // Handle media deletion
  const handleDeleteMedia = async (mediaId: string) => {
    try {
      await deleteMutation.mutateAsync(mediaId);

      // Invalidate media cache to refresh the list
      queryClient.invalidateQueries({ queryKey: ['/api/media'] });

      toast({
        title: "Media deleted",
        description: "The file has been successfully deleted.",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to delete the file. Please try again.",
        variant: "destructive",
      });
    }
  };

  // Helper function to format file size
  const formatFileSize = (bytes: number): string => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return Math.round(bytes / Math.pow(k, i) * 100) / 100 + ' ' + sizes[i];
  };

  const getFileIcon = (type: string) => {
    switch (type) {
      case "image": return ImageIcon;
      case "video": return Video;
      case "document": return FileText;
      default: return FileText;
    }
  };

  const getFileTypeColor = (type: string) => {
    switch (type) {
      case "image": return "text-green-500";
      case "video": return "text-purple-500";
      case "document": return "text-blue-500";
      default: return "text-gray-500";
    }
  };

  // Show loading state
  if (isLoading) {
    return (
      <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 xl:grid-cols-6 gap-3 md:gap-4">
        {Array.from({ length: 12 }).map((_, i) => (
          <LoadingCard key={i} className="aspect-square" />
        ))}
      </div>
    );
  }

  // Show error state
  if (error) {
    toast({
      title: "Connection Error",
      description: "Unable to load media files from server.",
      variant: "destructive",
    });
  }

  const filteredFiles = mediaFiles.filter((file: any) => {
    const matchesSearch = file.name.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesType = typeFilter === "all" || file.type === typeFilter;
    return matchesSearch && matchesType;
  });

  const GridView = () => (
    <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 xl:grid-cols-6 gap-3 md:gap-4">
      {filteredFiles.map((file: any) => {
        const FileIcon = getFileIcon(file.type);
        return (
          <Card 
            key={file.id} 
            className="hover-elevate cursor-pointer"
            onClick={() => {
              onSelectFile?.(file);
            }}
            data-testid={`file-card-${file.id}`}
          >
            <CardContent className="p-2 md:p-3">
              <div className="aspect-square rounded mb-3 bg-muted flex items-center justify-center overflow-hidden">
                {file.thumbnail ? (
                  <img 
                    src={file.thumbnail} 
                    alt={file.name}
                    className="w-full h-full object-cover"
                  />
                ) : (
                  <FileIcon className={`w-8 h-8 ${getFileTypeColor(file.type)}`} />
                )}
              </div>

              <div className="space-y-1">
                <p className="text-sm font-medium truncate" title={file.name}>
                  {file.name}
                </p>
                <div className="flex items-center justify-between text-xs text-muted-foreground">
                  <span>{file.size}</span>
                  {file.used && (
                    <Badge variant="secondary" className="text-xs px-1">In use</Badge>
                  )}
                </div>
              </div>

              <DropdownMenu>
                <DropdownMenuTrigger asChild onClick={(e) => e.stopPropagation()}>
                  <Button 
                    variant="ghost" 
                    size="icon" 
                    className="absolute top-2 right-2 w-6 h-6 opacity-0 group-hover:opacity-100"
                    data-testid={`file-actions-${file.id}`}
                  >
                    <MoreHorizontal className="w-3 h-3" />
                  </Button>
                </DropdownMenuTrigger>
                <DropdownMenuContent align="end">
                  <DropdownMenuItem data-testid={`menu-download-${file.id}`}>
                    <Download className="w-4 h-4 mr-2" />
                    Download
                  </DropdownMenuItem>
                  <DropdownMenuItem 
                    className="text-destructive" 
                    data-testid={`menu-delete-${file.id}`}
                    onClick={(e) => {
                      e.stopPropagation();
                      handleDeleteMedia(file.id);
                    }}
                  >
                    <Trash2 className="w-4 h-4 mr-2" />
                    Delete
                  </DropdownMenuItem>
                </DropdownMenuContent>
              </DropdownMenu>
            </CardContent>
          </Card>
        );
      })}
    </div>
  );

  const ListView = () => (
    <div className="space-y-2">
      {filteredFiles.map((file: any) => {
        const FileIcon = getFileIcon(file.type);
        return (
          <Card 
            key={file.id} 
            className="hover-elevate cursor-pointer"
            onClick={() => {
              onSelectFile?.(file);
            }}
            data-testid={`file-row-${file.id}`}
          >
            <CardContent className="p-3 md:p-4">
              <div className="flex items-center gap-4">
                <div className="w-12 h-12 rounded bg-muted flex items-center justify-center flex-shrink-0 overflow-hidden">
                  {file.thumbnail ? (
                    <img 
                      src={file.thumbnail} 
                      alt={file.name}
                      className="w-full h-full object-cover"
                    />
                  ) : (
                    <FileIcon className={`w-6 h-6 ${getFileTypeColor(file.type)}`} />
                  )}
                </div>

                <div className="flex-1 min-w-0">
                  <p className="font-medium truncate">{file.name}</p>
                  <div className="flex items-center gap-4 text-sm text-muted-foreground">
                    <span>{file.size}</span>
                    {file.dimensions && <span>{file.dimensions}</span>}
                    <span>{new Date(file.uploadDate).toLocaleDateString()}</span>
                  </div>
                </div>

                <div className="flex items-center gap-2">
                  {file.used && (
                    <Badge variant="secondary">In use</Badge>
                  )}

                  <DropdownMenu>
                    <DropdownMenuTrigger asChild onClick={(e) => e.stopPropagation()}>
                      <Button variant="ghost" size="icon" data-testid={`file-actions-list-${file.id}`}>
                        <MoreHorizontal className="w-4 h-4" />
                      </Button>
                    </DropdownMenuTrigger>
                    <DropdownMenuContent align="end">
                      <DropdownMenuItem>
                        <Download className="w-4 h-4 mr-2" />
                        Download
                      </DropdownMenuItem>
                      <DropdownMenuItem 
                        className="text-destructive"
                        onClick={(e) => {
                          e.stopPropagation();
                          handleDeleteMedia(file.id);
                        }}
                      >
                        <Trash2 className="w-4 h-4 mr-2" />
                        Delete
                      </DropdownMenuItem>
                    </DropdownMenuContent>
                  </DropdownMenu>
                </div>
              </div>
            </CardContent>
          </Card>
        );
      })}
    </div>
  );

  return (
    <div className="p-4 md:p-6 space-y-4 md:space-y-6">
      {/* Header */}
      <div className="flex items-start justify-between gap-4">
        <div>
          <h1 className="text-2xl md:text-3xl font-semibold">Media Library</h1>
          <p className="text-muted-foreground text-sm md:text-base">Manage your images, videos, and documents</p>
        </div>
        <Button onClick={() => setLocation("/upload")} data-testid="button-upload-media" size="sm" className="shrink-0">
          <Upload className="w-4 h-4 sm:mr-2" />
          <span className="hidden sm:inline">Upload Files</span>
        </Button>
      </div>

      {/* Filters and Controls */}
      <Card>
        <CardContent className="p-3 md:p-4">
          <div className="flex flex-col gap-4">
            <div className="flex flex-col sm:flex-row gap-3 sm:gap-4">
              <div className="relative flex-1">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-muted-foreground" />
                <Input
                  placeholder="Search files..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-10"
                  data-testid="input-media-search"
                />
              </div>

              <Select value={typeFilter} onValueChange={setTypeFilter}>
                <SelectTrigger className="w-full sm:w-[140px]" data-testid="select-type-filter">
                  <Filter className="w-4 h-4 mr-2" />
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Types</SelectItem>
                  <SelectItem value="image">Images</SelectItem>
                  <SelectItem value="video">Videos</SelectItem>
                  <SelectItem value="document">Documents</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div className="flex gap-2 justify-center sm:justify-end">
              <Button
                variant={viewMode === "grid" ? "default" : "outline"}
                size="icon"
                onClick={() => setViewMode("grid")}
                data-testid="button-grid-view"
              >
                <Grid3x3 className="w-4 h-4" />
              </Button>
              <Button
                variant={viewMode === "list" ? "default" : "outline"}
                size="icon"
                onClick={() => setViewMode("list")}
                data-testid="button-list-view"
              >
                <List className="w-4 h-4" />
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Media Content */}
      <div>
        {filteredFiles.length > 0 ? (
          viewMode === "grid" ? <GridView /> : <ListView />
        ) : (
          <Card>
            <CardContent className="text-center py-8 md:py-12">
              <ImageIcon className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
              <h3 className="text-lg font-medium mb-2">No files found</h3>
              <p className="text-muted-foreground mb-4">
                {searchQuery || typeFilter !== "all" 
                  ? "Try adjusting your search or filters"
                  : "Upload your first file to get started"}
              </p>
              {!searchQuery && typeFilter === "all" && (
                <Button onClick={() => setLocation("/upload")} data-testid="button-upload-first">
                  <Upload className="w-4 h-4 mr-2" />
                  Upload Files
                </Button>
              )}
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

client/src/components/MobileNavigation.t.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Sheet, SheetContent, SheetClose, SheetHeader, SheetTitle, SheetDescription } from "@/components/ui/sheet";
import { Menu, X } from "lucide-react";
import { useLocation } from "wouter";
import SimpleMobileNav from "./SimpleMobileNav";

interface MobileNavigationProps {
  currentPage?: string;
}

export default function MobileNavigation({ currentPage = "dashboard" }: MobileNavigationProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [location] = useLocation();

  // Close mobile menu when route changes
  useEffect(() => {
    if (isOpen) {
      if (import.meta.env.MODE === 'development') {
        console.log('ðŸ“± [MOBILE NAV] Route changed, closing mobile menu:', {
          newLocation: location,
          timestamp: new Date().toISOString()
        });
      }
      setIsOpen(false);
    }
  }, [location]); // Removed isOpen from dependencies to prevent immediate closing

  // Debug logging for mobile menu state
  useEffect(() => {
    if (import.meta.env.MODE === 'development') {
      console.log('ðŸ“± [MOBILE NAV] Menu state changed:', {
        isOpen,
        currentPage,
        location,
        timestamp: new Date().toISOString()
      });
    }
  }, [isOpen, currentPage, location]);

  return (
    <>
      {/* Custom hamburger button with proper icon state */}
      <Button 
        variant="ghost" 
        size="icon" 
        className="lg:hidden"
        onClick={() => setIsOpen(!isOpen)}
        aria-label={isOpen ? "Close menu" : "Open menu"}
        data-testid="button-mobile-menu"
      >
        {isOpen ? <X className="w-5 h-5" /> : <Menu className="w-5 h-5" />}
      </Button>

      <Sheet open={isOpen} onOpenChange={setIsOpen}>
        <SheetContent 
          side="left" 
          className="p-0 w-[min(20rem,90vw)] h-[100dvh] pt-[env(safe-area-inset-top)] pb-[env(safe-area-inset-bottom)]"
          data-testid="sheet-mobile-nav"
          aria-describedby="mobile-nav-description"
        >
          <SheetHeader className="p-4 border-b">
            <SheetTitle className="text-left font-bold text-foreground">
              Penkora
            </SheetTitle>
            <SheetDescription id="mobile-nav-description" className="sr-only">
              Navigation menu for Penkora CMS with content management options
            </SheetDescription>
          </SheetHeader>
          <div className="overflow-y-auto flex-1">
            <SimpleMobileNav 
              activeItem={currentPage}
              onNavigate={() => setIsOpen(false)}
            />
          </div>
        </SheetContent>
      </Sheet>
    </>
  );
}

client/src/components/PostsList.tsx.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { 
  Search, 
  Filter, 
  Plus, 
  Edit, 
  Trash2, 
  Eye, 
  Calendar,
  MoreHorizontal,
  FileText
} from "lucide-react";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";

interface PostsListProps {
  onCreatePost?: () => void;
  onEditPost?: (id: string) => void;
}

export default function PostsList({ onCreatePost, onEditPost }: PostsListProps) {
  const [searchQuery, setSearchQuery] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");

  // TODO: Remove mock data
  const posts = [
    {
      id: "1",
      title: "Getting Started with React Development",
      excerpt: "A comprehensive guide to building modern React applications...",
      status: "published",
      author: "John Doe",
      category: "Tutorial",
      publishDate: "2024-01-15",
      views: 1247,
      featured: true
    },
    {
      id: "2", 
      title: "Advanced TypeScript Patterns",
      excerpt: "Exploring advanced TypeScript features and design patterns...",
      status: "draft",
      author: "Jane Smith",
      category: "Programming",
      publishDate: null,
      views: 0,
      featured: false
    },
    {
      id: "3",
      title: "Database Design Best Practices", 
      excerpt: "Learn how to design scalable and efficient database schemas...",
      status: "scheduled",
      author: "Mike Johnson",
      category: "Database",
      publishDate: "2024-01-20",
      views: 0,
      featured: false
    },
    {
      id: "4",
      title: "Modern CSS Techniques",
      excerpt: "CSS Grid, Flexbox, and modern layout techniques explained...",
      status: "published",
      author: "Sarah Wilson",
      category: "CSS",
      publishDate: "2024-01-10",
      views: 856,
      featured: true
    }
  ];

  const getStatusBadge = (status: string) => {
    const variants = {
      published: { variant: "default" as const, color: "bg-green-500" },
      draft: { variant: "secondary" as const, color: "bg-yellow-500" },
      scheduled: { variant: "outline" as const, color: "bg-blue-500" }
    };

    return variants[status as keyof typeof variants] || variants.draft;
  };

  const filteredPosts = posts.filter(post => {
    const matchesSearch = post.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         post.excerpt.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesStatus = statusFilter === "all" || post.status === statusFilter;
    return matchesSearch && matchesStatus;
  });

  return (
    <div className="p-4 md:p-6 space-y-4 md:space-y-6">
      {/* Header */}
      <div className="flex items-start justify-between gap-4">
        <div>
          <h1 className="text-2xl md:text-3xl font-semibold">Posts</h1>
          <p className="text-muted-foreground text-sm md:text-base">Manage your content and articles</p>
        </div>
        <Button onClick={onCreatePost} data-testid="button-create-post" size="sm" className="shrink-0">
          <Plus className="w-4 h-4 sm:mr-2" />
          <span className="hidden sm:inline">New Post</span>
        </Button>
      </div>

      {/* Filters */}
      <Card>
        <CardContent className="p-3 md:p-4">
          <div className="flex flex-col sm:flex-row gap-3 sm:gap-4">
            <div className="relative flex-1">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-muted-foreground" />
              <Input
                placeholder="Search posts..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10"
                data-testid="input-posts-search"
              />
            </div>
            <div className="flex gap-2 shrink-0">
              <Select value={statusFilter} onValueChange={setStatusFilter}>
                <SelectTrigger className="w-full sm:w-[140px]" data-testid="select-status-filter">
                  <Filter className="w-4 h-4 mr-2" />
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Status</SelectItem>
                  <SelectItem value="published">Published</SelectItem>
                  <SelectItem value="draft">Draft</SelectItem>
                  <SelectItem value="scheduled">Scheduled</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Posts List */}
      <div className="space-y-4">
        {filteredPosts.map((post) => (
          <Card key={post.id} className="hover-elevate" data-testid={`post-card-${post.id}`}>
            <CardContent className="p-4 md:p-6">
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <div className="flex items-center gap-3 mb-2">
                    <h3 className="text-lg font-semibold">{post.title}</h3>
                    {post.featured && (
                      <Badge variant="outline" className="text-xs">Featured</Badge>
                    )}
                    <Badge 
                      variant={getStatusBadge(post.status).variant}
                      className="text-xs capitalize"
                    >
                      {post.status}
                    </Badge>
                  </div>

                  <p className="text-muted-foreground mb-3 line-clamp-2">
                    {post.excerpt}
                  </p>

                  <div className="flex items-center gap-2 md:gap-4 text-xs md:text-sm text-muted-foreground flex-wrap">
                    <span>By {post.author}</span>
                    <span>â€¢</span>
                    <span>{post.category}</span>
                    {post.publishDate && (
                      <>
                        <span>â€¢</span>
                        <span className="flex items-center gap-1">
                          <Calendar className="w-3 h-3" />
                          {new Date(post.publishDate).toLocaleDateString()}
                        </span>
                      </>
                    )}
                    <span>â€¢</span>
                    <span className="flex items-center gap-1">
                      <Eye className="w-3 h-3" />
                      {post.views.toLocaleString()} views
                    </span>
                  </div>
                </div>

                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button variant="ghost" size="icon" data-testid={`button-post-actions-${post.id}`}>
                      <MoreHorizontal className="w-4 h-4" />
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent align="end">
                    <DropdownMenuItem 
                      onClick={() => {
                        onEditPost?.(post.id);
                        console.log(`Edit post: ${post.id}`);
                      }}
                      data-testid={`menu-edit-${post.id}`}
                    >
                      <Edit className="w-4 h-4 mr-2" />
                      Edit
                    </DropdownMenuItem>
                    <DropdownMenuItem data-testid={`menu-view-${post.id}`}>
                      <Eye className="w-4 h-4 mr-2" />
                      Preview
                    </DropdownMenuItem>
                    <DropdownMenuItem className="text-destructive" data-testid={`menu-delete-${post.id}`}>
                      <Trash2 className="w-4 h-4 mr-2" />
                      Delete
                    </DropdownMenuItem>
                  </DropdownMenuContent>
                </DropdownMenu>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {filteredPosts.length === 0 && (
        <Card>
          <CardContent className="text-center py-8 md:py-12">
            <FileText className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
            <h3 className="text-lg font-medium mb-2">No posts found</h3>
            <p className="text-muted-foreground mb-4">
              {searchQuery || statusFilter !== "all" 
                ? "Try adjusting your search or filters"
                : "Get started by creating your first post"}
            </p>
            {!searchQuery && statusFilter === "all" && (
              <Button onClick={onCreatePost} data-testid="button-create-first-post">
                <Plus className="w-4 h-4 mr-2" />
                Create your first post
              </Button>
            )}
          </CardContent>
        </Card>
      )}
    </div>
  );
}

