        {items.map((item) => (
          <Link
            key={item.url}
            href={item.url}
            onClick={() => {
              if (import.meta.env.DEV) {
                console.log('ðŸ”— [MOBILE NAV] Navigation clicked:', {
                  from: location,
                  to: item.url,
                  title: item.title,
                  timestamp: new Date().toISOString()
                });
