import { useState } from "react";
import { Search, Plus, MoreHorizontal, Edit, Trash2, FolderPlus, Tag } from "lucide-react";
import { useCategories, useCreateCategory, useUpdateCategory, useDeleteCategory } from "@/hooks/useCategories";
import { useTags, useCreateTag, useUpdateTag, useDeleteTag } from "@/hooks/useTags";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { 
  Page, 
  PageHeader, 
  PageTitle, 
  PageToolbar, 
  PageBody, 
  PageActions,
  FAB
} from "@/components/ui/page";
import { EmptyState } from "@/components/ui/empty-state";

interface Category {
  id: string;
  name: string;
  slug: string;
  description?: string;
  postCount: number;
  color?: string;
}

interface Tag {
  id: string;
  name: string;
  slug: string;
  postCount: number;
}

interface CategoriesPageProps {
  onCreateCategory?: (data: any) => void;
  onEditCategory?: (id: string, data: any) => void;
  onDeleteCategory?: (id: string) => void;
  onCreateTag?: (data: any) => void;
  onEditTag?: (id: string, data: any) => void;
  onDeleteTag?: (id: string) => void;
}

// Mock data
const mockCategories: Category[] = [
  { id: "1", name: "Development", slug: "development", description: "Programming and software development content", postCount: 45, color: "#3b82f6" },
  { id: "2", name: "Design", slug: "design", description: "UI/UX design and visual content", postCount: 23, color: "#8b5cf6" },
  { id: "3", name: "Technology", slug: "technology", description: "Latest tech news and trends", postCount: 31, color: "#06b6d4" },
];

const mockTags: Tag[] = [
  { id: "1", name: "React", slug: "react", postCount: 18 },
  { id: "2", name: "TypeScript", slug: "typescript", postCount: 15 },
  { id: "3", name: "CSS", slug: "css", postCount: 12 },
  { id: "4", name: "JavaScript", slug: "javascript", postCount: 22 },
  { id: "5", name: "Tutorial", slug: "tutorial", postCount: 8 },
];

export default function CategoriesPage({ 
  onCreateCategory = (data) => console.log("Create category:", data),
  onEditCategory = (id, data) => console.log("Edit category:", id, data),
  onDeleteCategory = (id) => console.log("Delete category:", id),
  onCreateTag = (data) => console.log("Create tag:", data),
  onEditTag = (id, data) => console.log("Edit tag:", id, data),
  onDeleteTag = (id) => console.log("Delete tag:", id)
}: CategoriesPageProps) {
  const [searchQuery, setSearchQuery] = useState("");
  const [activeTab, setActiveTab] = useState("categories");
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [newItemData, setNewItemData] = useState({ name: "", description: "" });

  const { toast } = useToast();
  const { data: categories = [], isLoading: categoriesLoading } = useCategories();
  const { data: tags = [], isLoading: tagsLoading } = useTags();
  const createCategoryMutation = useCreateCategory();
  const updateCategoryMutation = useUpdateCategory();
  const deleteCategoryMutation = useDeleteCategory();
  const createTagMutation = useCreateTag();
  const updateTagMutation = useUpdateTag();
  const deleteTagMutation = useDeleteTag();

  const filteredCategories = categories.filter((category: any) =>
    category.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    category.description?.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const filteredTags = tags.filter((tag: any) =>
    tag.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const handleCreateItem = async () => {
    try {
      const slug = newItemData.name.toLowerCase().replace(/[^a-z0-9]+/g, "-").replace(/(^-|-$)/g, "");

      if (activeTab === "categories") {
        await createCategoryMutation.mutateAsync({
          name: newItemData.name,
          description: newItemData.description,
          slug
        });
        toast({
          title: "Success",
          description: "Category created successfully.",
        });
        onCreateCategory({
          name: newItemData.name,
          description: newItemData.description,
          slug
        });
      } else {
        await createTagMutation.mutateAsync({
          name: newItemData.name,
          slug
        });
        toast({
          title: "Success",
          description: "Tag created successfully.",
        });
        onCreateTag({
          name: newItemData.name,
          slug
        });
      }
      setNewItemData({ name: "", description: "" });
      setIsCreateDialogOpen(false);
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || `Failed to create ${activeTab.slice(0, -1)}. Please try again.`,
        variant: "destructive",
      });
    }
  };

  const handleDeleteCategory = async (id: string) => {
    try {
      await deleteCategoryMutation.mutateAsync(id);
      toast({
        title: "Success",
        description: "Category deleted successfully.",
      });
      onDeleteCategory(id);
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to delete category. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleDeleteTag = async (id: string) => {
    try {
      await deleteTagMutation.mutateAsync(id);
      toast({
        title: "Success",
        description: "Tag deleted successfully.",
      });
      onDeleteTag(id);
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to delete tag. Please try again.",
        variant: "destructive",
      });
    }
  };

  const CategoryCard = ({ category }: { category: Category }) => (
    <Card className="hover-elevate" data-testid={`category-card-${category.id}`}>
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between">
          <div className="flex items-center gap-3 min-w-0 flex-1">
            <div 
              className="w-4 h-4 rounded-full shrink-0"
              style={{ backgroundColor: category.color }}
            />
            <div className="min-w-0 flex-1">
              <CardTitle className="text-base line-clamp-1">{category.name}</CardTitle>
              <p className="text-sm text-muted-foreground mt-1">
                /{category.slug}
              </p>
            </div>
          </div>
          <div className="flex items-center gap-2">
            <Badge variant="secondary" className="text-xs">
              {category.postCount} posts
            </Badge>
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button variant="ghost" size="icon" className="shrink-0" data-testid={`category-menu-${category.id}`}>
                  <MoreHorizontal className="h-4 w-4" />
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end">
                <DropdownMenuItem onClick={() => onEditCategory(category.id, category)}>
                  <Edit className="h-4 w-4 mr-2" />
                  Edit
                </DropdownMenuItem>
                <DropdownMenuItem 
                  onClick={() => handleDeleteCategory(category.id)}
                  className="text-destructive focus:text-destructive"
                >
                  <Trash2 className="h-4 w-4 mr-2" />
                  Delete
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
          </div>
        </div>
      </CardHeader>
      {category.description && (
        <CardContent className="pt-0">
          <p className="text-sm text-muted-foreground line-clamp-2">
            {category.description}
          </p>
        </CardContent>
      )}
    </Card>
  );

  const TagCard = ({ tag }: { tag: Tag }) => (
    <Card className="hover-elevate" data-testid={`tag-card-${tag.id}`}>
      <CardHeader>
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2 min-w-0 flex-1">
            <Tag className="h-4 w-4 text-muted-foreground shrink-0" />
            <div className="min-w-0 flex-1">
              <CardTitle className="text-base line-clamp-1">{tag.name}</CardTitle>
              <p className="text-sm text-muted-foreground">
                /{tag.slug}
              </p>
            </div>
          </div>
          <div className="flex items-center gap-2">
            <Badge variant="secondary" className="text-xs">
              {tag.postCount}
            </Badge>
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button variant="ghost" size="icon" className="shrink-0" data-testid={`tag-menu-${tag.id}`}>
                  <MoreHorizontal className="h-4 w-4" />
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end">
                <DropdownMenuItem onClick={() => onEditTag(tag.id, tag)}>
                  <Edit className="h-4 w-4 mr-2" />
                  Edit
                </DropdownMenuItem>
                <DropdownMenuItem 
                  onClick={() => handleDeleteTag(tag.id)}
                  className="text-destructive focus:text-destructive"
                >
                  <Trash2 className="h-4 w-4 mr-2" />
                  Delete
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
          </div>
        </div>
      </CardHeader>
    </Card>
  );

  const CreateDialog = () => (
    <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
      <DialogContent data-testid="create-dialog">
        <DialogHeader>
          <DialogTitle>
            Create New {activeTab === "categories" ? "Category" : "Tag"}
          </DialogTitle>
        </DialogHeader>
        <div className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="name">Name *</Label>
            <Input
              id="name"
              placeholder={`Enter ${activeTab === "categories" ? "category" : "tag"} name...`}
              value={newItemData.name}
              onChange={(e) => setNewItemData(prev => ({ ...prev, name: e.target.value }))}
              data-testid="item-name"
            />
          </div>
          {activeTab === "categories" && (
            <div className="space-y-2">
              <Label htmlFor="description">Description</Label>
              <Textarea
                id="description"
                placeholder="Enter category description..."
                value={newItemData.description}
                onChange={(e) => setNewItemData(prev => ({ ...prev, description: e.target.value }))}
                className="h-20"
                data-testid="item-description"
              />
            </div>
          )}
          <div className="flex justify-end gap-2">
            <Button variant="outline" onClick={() => setIsCreateDialogOpen(false)}>
              Cancel
            </Button>
            <Button onClick={handleCreateItem} disabled={!newItemData.name.trim()}>
              Create {activeTab === "categories" ? "Category" : "Tag"}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );

  return (
    <Page data-testid="categories-page">
      <PageHeader>
        <div className="px-4 md:px-6">
          <PageToolbar>
            <PageTitle>
              Categories & Tags
            </PageTitle>
            <PageActions>
              <div className="hidden md:flex items-center gap-2">
                <Button onClick={() => setIsCreateDialogOpen(true)} data-testid="create-desktop">
                  <Plus className="h-4 w-4 mr-2" />
                  New {activeTab === "categories" ? "Category" : "Tag"}
                </Button>
              </div>
            </PageActions>
          </PageToolbar>

          <div className="mt-4">
            <div className="relative max-w-md">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
              <Input
                placeholder={`Search ${activeTab}...`}
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-9"
                data-testid="search-input"
              />
            </div>
          </div>
        </div>
      </PageHeader>

      <PageBody className="px-4 md:px-6">
        <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-6">
          <TabsList className="grid w-full grid-cols-2 max-w-md">
            <TabsTrigger value="categories" className="flex items-center gap-2">
              <FolderPlus className="h-4 w-4" />
              Categories ({mockCategories.length})
            </TabsTrigger>
            <TabsTrigger value="tags" className="flex items-center gap-2">
              <Tag className="h-4 w-4" />
              Tags ({mockTags.length})
            </TabsTrigger>
          </TabsList>

          <TabsContent value="categories" className="space-y-4">
            {filteredCategories.length === 0 ? (
              searchQuery ? (
                <div className="text-center py-12">
                  <h3 className="text-lg font-semibold mb-2">No categories found</h3>
                  <p className="text-sm text-muted-foreground">
                    Try adjusting your search terms.
                  </p>
                </div>
              ) : (
                <EmptyState
                  icon={<FolderPlus className="h-8 w-8 text-muted-foreground" />}
                  title="No categories yet"
                  description="Create your first category to organize your posts."
                  action={{
                    label: "Create Category",
                    onClick: () => setIsCreateDialogOpen(true)
                  }}
                />
              )
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {filteredCategories.map(category => (
                  <CategoryCard key={category.id} category={category} />
                ))}
              </div>
            )}
          </TabsContent>

          <TabsContent value="tags" className="space-y-4">
            {filteredTags.length === 0 ? (
              searchQuery ? (
                <div className="text-center py-12">
                  <h3 className="text-lg font-semibold mb-2">No tags found</h3>
                  <p className="text-sm text-muted-foreground">
                    Try adjusting your search terms.
                  </p>
                </div>
              ) : (
                <EmptyState
                  icon={<Tag className="h-8 w-8 text-muted-foreground" />}
                  title="No tags yet"
                  description="Create your first tag to help categorize your content."
                  action={{
                    label: "Create Tag",
                    onClick: () => setIsCreateDialogOpen(true)
                  }}
                />
              )
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                {filteredTags.map(tag => (
                  <TagCard key={tag.id} tag={tag} />
                ))}
              </div>
            )}
          </TabsContent>
        </Tabs>
      </PageBody>

      {/* Mobile FAB */}
      <FAB 
        onClick={() => setIsCreateDialogOpen(true)}
        aria-label={`Create new ${activeTab === "categories" ? "category" : "tag"}`}
        data-testid="create-fab"
      >
        New {activeTab === "categories" ? "Category" : "Tag"}
      </FAB>

      <CreateDialog />
    </Page>
  );
}

client/src/components/MediaLibraryPage.t.content:
import { useState } from "react";
import { Search, Grid, List, Upload, Filter, MoreHorizontal, Download, Eye, Trash2, Folder, Image, Video, FileText } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { Card, CardContent } from "@/components/ui/card";
import { Checkbox } from "@/components/ui/checkbox";
import { 
  Page, 
  PageHeader, 
  PageTitle, 
  PageToolbar, 
  PageBody, 
  PageActions,
  ActionBar,
  FAB,
  FilterSheet
} from "@/components/ui/page";
import { EmptyState } from "@/components/ui/empty-state";
import { LoadingGrid } from "@/components/ui/loading";

interface MediaFile {
  id: string;
  name: string;
  type: "image" | "video" | "audio" | "document";
  url: string;
  size: number;
  uploadDate: string;
  dimensions?: { width: number; height: number };
  duration?: number;
  mimeType: string;
}

interface MediaLibraryPageProps {
  onUpload?: () => void;
  onSelect?: (file: MediaFile) => void;
  onDelete?: (ids: string[]) => void;
  onDownload?: (ids: string[]) => void;
}

// Mock data
const mockFiles: MediaFile[] = [
  {
    id: "1",
    name: "hero-banner.jpg",
    type: "image",
    url: "/images/hero-banner.jpg",
    size: 245760,
    uploadDate: "2024-03-15",
    dimensions: { width: 1920, height: 1080 },
    mimeType: "image/jpeg"
  },
  {
    id: "2", 
    name: "product-demo.mp4",
    type: "video",
    url: "/videos/product-demo.mp4",
    size: 15728640,
    uploadDate: "2024-03-14",
    dimensions: { width: 1280, height: 720 },
    duration: 120,
    mimeType: "video/mp4"
  },
  {
    id: "3",
    name: "user-guide.pdf",
    type: "document",
    url: "/documents/user-guide.pdf",
    size: 1048576,
    uploadDate: "2024-03-13",
    mimeType: "application/pdf"
  },
  {
    id: "4",
    name: "logo-variants.png",
    type: "image",
    url: "/images/logo-variants.png",
    size: 89600,
    uploadDate: "2024-03-12",
    dimensions: { width: 800, height: 600 },
    mimeType: "image/png"
  },
  {
    id: "5",
    name: "background-music.mp3",
    type: "audio",
    url: "/audio/background-music.mp3",
    size: 5242880,
    uploadDate: "2024-03-11",
    duration: 180,
    mimeType: "audio/mpeg"
  }
];

export default function MediaLibraryPage({ 
  onUpload = () => console.log("Upload files"),
  onSelect = (file) => console.log("Select file:", file),
  onDelete = (ids) => console.log("Delete files:", ids),
  onDownload = (ids) => console.log("Download files:", ids)
}: MediaLibraryPageProps) {
  const [searchQuery, setSearchQuery] = useState("");
  const [typeFilter, setTypeFilter] = useState<string>("all");
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid");
  const [selectedFiles, setSelectedFiles] = useState<string[]>([]);
  const [isLoading, setIsLoading] = useState(false);

  // Filter files based on search and filters
  const filteredFiles = mockFiles.filter(file => {
    const matchesSearch = file.name.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesType = typeFilter === "all" || file.type === typeFilter;
    return matchesSearch && matchesType;
  });

  const handleSelectFile = (fileId: string, checked: boolean) => {
    if (checked) {
      setSelectedFiles([...selectedFiles, fileId]);
    } else {
      setSelectedFiles(selectedFiles.filter(id => id !== fileId));
    }
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      setSelectedFiles(filteredFiles.map(file => file.id));
    } else {
      setSelectedFiles([]);
    }
  };

  const formatFileSize = (bytes: number) => {
    const units = ['B', 'KB', 'MB', 'GB'];
    let size = bytes;
    let unitIndex = 0;

    while (size >= 1024 && unitIndex < units.length - 1) {
      size /= 1024;
      unitIndex++;
    }

    return `${size.toFixed(1)} ${units[unitIndex]}`;
  };

  const getFileIcon = (type: MediaFile["type"]) => {
    const icons = {
      image: Image,
      video: Video,
      audio: Video, // Using video icon for audio
      document: FileText
    };
    return icons[type] || FileText;
  };

  const getFileTypeColor = (type: MediaFile["type"]) => {
    const colors = {
      image: "bg-blue-500",
      video: "bg-purple-500", 
      audio: "bg-green-500",
      document: "bg-orange-500"
    };
    return colors[type] || "bg-gray-500";
  };

  const MediaGridItem = ({ file }: { file: MediaFile }) => {
    const Icon = getFileIcon(file.type);

    return (
      <Card className="hover-elevate group" data-testid={`media-item-${file.id}`}>
        <CardContent className="p-3">
          <div className="flex items-start justify-between mb-2">
            <Checkbox
              checked={selectedFiles.includes(file.id)}
              onCheckedChange={(checked) => handleSelectFile(file.id, !!checked)}
              data-testid={`file-checkbox-${file.id}`}
            />
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button variant="ghost" size="icon" className="shrink-0 opacity-0 group-hover:opacity-100" data-testid={`file-menu-${file.id}`}>
                  <MoreHorizontal className="h-4 w-4" />
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end">
                <DropdownMenuItem onClick={() => onSelect(file)}>
                  <Eye className="h-4 w-4 mr-2" />
                  View
                </DropdownMenuItem>
                <DropdownMenuItem onClick={() => onDownload([file.id])}>
                  <Download className="h-4 w-4 mr-2" />
                  Download
                </DropdownMenuItem>
                <DropdownMenuItem 
                  onClick={() => onDelete([file.id])}
                  className="text-destructive focus:text-destructive"
                >
                  <Trash2 className="h-4 w-4 mr-2" />
                  Delete
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
          </div>

          <div className="aspect-square bg-muted rounded-lg flex items-center justify-center mb-3 relative overflow-hidden">
            {file.type === "image" ? (
              <img 
                src={file.url} 
                alt={file.name}
                className="w-full h-full object-cover"
                onError={(e) => {
                  const target = e.target as HTMLImageElement;
                  target.style.display = "none";
                  target.nextElementSibling?.classList.remove("hidden");
                }}
              />
            ) : null}
            <div className={`absolute inset-0 flex items-center justify-center ${file.type === "image" ? "hidden" : ""}`}>
              <div className={`p-3 rounded-full ${getFileTypeColor(file.type)}`}>
                <Icon className="h-6 w-6 text-white" />
              </div>
            </div>
            {file.duration && (
              <div className="absolute bottom-2 right-2 bg-black/75 text-white text-xs px-1.5 py-0.5 rounded">
                {Math.floor(file.duration / 60)}:{(file.duration % 60).toString().padStart(2, "0")}
              </div>
            )}
          </div>

          <div className="space-y-1">
            <p className="text-sm font-medium line-clamp-1" title={file.name}>
              {file.name}
            </p>
            <div className="flex items-center justify-between text-xs text-muted-foreground">
              <span>{formatFileSize(file.size)}</span>
              {file.dimensions && (
                <span>{file.dimensions.width}Ã—{file.dimensions.height}</span>
              )}
            </div>
            <p className="text-xs text-muted-foreground">
              {new Date(file.uploadDate).toLocaleDateString()}
            </p>
          </div>
        </CardContent>
      </Card>
    );
  };

  const MediaListItem = ({ file }: { file: MediaFile }) => {
    const Icon = getFileIcon(file.type);

    return (
      <div className="flex items-center gap-3 p-3 border rounded-lg hover-elevate group" data-testid={`media-item-${file.id}`}>
        <Checkbox
          checked={selectedFiles.includes(file.id)}
          onCheckedChange={(checked) => handleSelectFile(file.id, !!checked)}
          data-testid={`file-checkbox-${file.id}`}
        />

        <div className={`p-2 rounded ${getFileTypeColor(file.type)}`}>
          <Icon className="h-4 w-4 text-white" />
        </div>

        <div className="flex-1 min-w-0">
          <p className="font-medium line-clamp-1">{file.name}</p>
          <div className="flex items-center gap-2 text-sm text-muted-foreground">
            <span>{formatFileSize(file.size)}</span>
            {file.dimensions && (
              <>
                <span>â€¢</span>
                <span>{file.dimensions.width}Ã—{file.dimensions.height}</span>
              </>
            )}
            {file.duration && (
              <>
                <span>â€¢</span>
                <span>{Math.floor(file.duration / 60)}:{(file.duration % 60).toString().padStart(2, "0")}</span>
              </>
            )}
            <span>â€¢</span>
            <span>{new Date(file.uploadDate).toLocaleDateString()}</span>
          </div>
        </div>

        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <Button variant="ghost" size="icon" className="shrink-0 opacity-0 group-hover:opacity-100" data-testid={`file-menu-${file.id}`}>
              <MoreHorizontal className="h-4 w-4" />
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent align="end">
            <DropdownMenuItem onClick={() => onSelect(file)}>
              <Eye className="h-4 w-4 mr-2" />
              View
            </DropdownMenuItem>
            <DropdownMenuItem onClick={() => onDownload([file.id])}>
              <Download className="h-4 w-4 mr-2" />
              Download
            </DropdownMenuItem>
            <DropdownMenuItem 
              onClick={() => onDelete([file.id])}
              className="text-destructive focus:text-destructive"
            >
              <Trash2 className="h-4 w-4 mr-2" />
              Delete
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>
      </div>
    );
  };

  const FilterControls = () => (
    <div className="space-y-4">
      <div className="space-y-2">
        <label className="text-sm font-medium">File Type</label>
        <Select value={typeFilter} onValueChange={setTypeFilter}>
          <SelectTrigger>
            <SelectValue />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Types</SelectItem>
            <SelectItem value="image">Images</SelectItem>
            <SelectItem value="video">Videos</SelectItem>
            <SelectItem value="audio">Audio</SelectItem>
            <SelectItem value="document">Documents</SelectItem>
          </SelectContent>
        </Select>
      </div>
    </div>
  );

  return (
    <Page data-testid="media-library-page">
      <PageHeader>
        <div className="px-4 md:px-6">
          <PageToolbar>
            <PageTitle badge="2.1k">
              Media Library
            </PageTitle>
            <PageActions>
              <div className="hidden md:flex items-center gap-2">
                <Button
                  variant={viewMode === "grid" ? "default" : "outline"}
                  size="icon"
                  onClick={() => setViewMode("grid")}
                  data-testid="view-grid"
                >
                  <Grid className="h-4 w-4" />
                </Button>
                <Button
                  variant={viewMode === "list" ? "default" : "outline"}
                  size="icon"
                  onClick={() => setViewMode("list")}
                  data-testid="view-list"
                >
                  <List className="h-4 w-4" />
                </Button>
                <Button onClick={onUpload} data-testid="upload-desktop">
                  <Upload className="h-4 w-4 mr-2" />
                  Upload
                </Button>
              </div>
            </PageActions>
          </PageToolbar>

          <div className="mt-4 flex flex-col gap-3 md:flex-row md:items-center md:justify-between">
            <div className="flex items-center gap-2 flex-1 max-w-md">
              <div className="relative flex-1">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
                <Input
                  placeholder="Search files..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-9"
                  data-testid="search-files"
                />
              </div>
              <FilterSheet title="Filter Files">
                <FilterControls />
              </FilterSheet>
            </div>

            {/* Desktop Filters */}
            <div className="hidden md:flex items-center gap-2">
              <Select value={typeFilter} onValueChange={setTypeFilter}>
                <SelectTrigger className="w-36">
                  <SelectValue placeholder="File Type" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Types</SelectItem>
                  <SelectItem value="image">Images</SelectItem>
                  <SelectItem value="video">Videos</SelectItem>
                  <SelectItem value="audio">Audio</SelectItem>
                  <SelectItem value="document">Documents</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
        </div>
      </PageHeader>

      <PageBody className="px-4 md:px-6">
        {isLoading ? (
          <LoadingGrid items={12} columns={viewMode === "grid" ? 4 : 1} />
        ) : filteredFiles.length === 0 ? (
          searchQuery || typeFilter !== "all" ? (
            <div className="text-center py-12">
              <h3 className="text-lg font-semibold mb-2">No files found</h3>
              <p className="text-sm text-muted-foreground">
                Try adjusting your search or filter criteria.
              </p>
            </div>
          ) : (
            <EmptyState
              icon={<Folder className="h-8 w-8 text-muted-foreground" />}
              title="No media files"
              description="Upload images, videos, and documents to your media library."
              action={{
                label: "Upload Files",
                onClick: onUpload
              }}
            />
          )
        ) : (
          <>
            {/* Selection Bar */}
            {selectedFiles.length > 0 && (
              <div className="bg-muted/50 border rounded-lg p-3 mb-4">
                <div className="flex items-center justify-between">
                  <span className="text-sm font-medium">
                    {selectedFiles.length} file{selectedFiles.length !== 1 ? 's' : ''} selected
                  </span>
                  <div className="flex items-center gap-2">
                    <Button variant="outline" size="sm" onClick={() => onDownload(selectedFiles)}>
                      <Download className="h-4 w-4 mr-1" />
                      Download
                    </Button>
                    <Button variant="outline" size="sm" className="text-destructive" onClick={() => onDelete(selectedFiles)}>
                      <Trash2 className="h-4 w-4 mr-1" />
                      Delete
                    </Button>
                  </div>
                </div>
              </div>
            )}

            {/* Files Content */}
            {viewMode === "grid" ? (
              <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4">
                {filteredFiles.map(file => (
                  <MediaGridItem key={file.id} file={file} />
                ))}
              </div>
            ) : (
              <div className="space-y-2">
                {filteredFiles.map(file => (
                  <MediaListItem key={file.id} file={file} />
                ))}
              </div>
            )}
          </>
        )}
      </PageBody>

      {/* Mobile FAB */}
      <FAB 
        onClick={onUpload}
        aria-label="Upload files"
        data-testid="upload-fab"
      >
        Upload
      </FAB>

      {/* Mobile Action Bar for bulk actions */}
      <ActionBar show={selectedFiles.length > 0}>
        <span className="text-sm font-medium">
          {selectedFiles.length} selected
        </span>
        <div className="flex gap-2">
          <Button variant="outline" size="sm" onClick={() => onDownload(selectedFiles)}>
            <Download className="h-4 w-4" />
          </Button>
          <Button variant="destructive" size="sm" onClick={() => onDelete(selectedFiles)}>
            <Trash2 className="h-4 w-4" />
          </Button>
        </div>
      </ActionBar>
    </Page>
  );
}

client/src/components/NewPostPage.tsx.content:
import { useState, useRef, useEffect, useCallback, useMemo } from "react";
import { useLocation } from "wouter";
import { ArrowLeft, Save, Eye, MoreHorizontal, ImagePlus, Tag, Settings, Globe } from "lucide-react";
import { useCreatePost } from "@/hooks/usePosts";
import { useToast } from "@/hooks/use-toast";
import { useCategories } from "@/hooks/useCategories";
import { useCreateTagsFromNames } from "@/hooks/useTags";
import { insertPostSchema } from "@shared/schema";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Switch } from "@/components/ui/switch";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Separator } from "@/components/ui/separator";
import { 
  Page, 
  PageHeader, 
  PageTitle, 
  PageToolbar, 
  PageBody, 
  PageActions,
  ActionBar
} from "@/components/ui/page";
import { RichTextEditor, type RichTextEditorRef } from "@/components/ui/rich-text-editor";
import { PostPreviewModal } from "./PostPreviewModal";

interface NewPostPageProps {
  mode?: 'create' | 'edit';
  initialData?: any;
  isLoading?: boolean;
  onSave?: (data: any) => void;
  onPublish?: (data: any) => void;
  onPreview?: (data: any) => void;
}

export default function NewPostPage({ 
  mode = 'create',
  initialData,
  isLoading: externalLoading = false,
  onSave,
  onPublish,
  onPreview = (data) => console.log("Preview post:", data)
}: NewPostPageProps) {
  const [, setLocation] = useLocation();
  const [activeTab, setActiveTab] = useState("content");
  const editorRef = useRef<RichTextEditorRef>(null);
  const { toast } = useToast();
  const createPostMutation = useCreatePost();
  const createTagsMutation = useCreateTagsFromNames();
  const { data: categories = [] } = useCategories();
  const isLoading = externalLoading || createPostMutation.isPending || createTagsMutation.isPending;
  const [formData, setFormData] = useState(() => initialData || {
    title: "",
    content: "",
    excerpt: "",
    category: "",
    tags: [] as string[],
    status: "draft",
    featured: false,
    allowComments: true,
    seoTitle: "",
    seoDescription: "",
    publishDate: ""
  });

  // Update form data when initialData changes (for edit mode)
  useEffect(() => {
    if (initialData && mode === 'edit') {
      setFormData(initialData);
      setIsDirty(false);
    }
  }, [initialData, mode]);

  const [newTag, setNewTag] = useState("");
  const [isDirty, setIsDirty] = useState(false);
  const [previewData, setPreviewData] = useState<any>(null);
  const [isPreviewOpen, setIsPreviewOpen] = useState(false);

  // Optimized update function to prevent unnecessary re-renders
  const updateField = useCallback((field: string, value: any) => {
    setFormData((prev: any) => {
      // Only update if the value actually changed
      if (prev[field] === value) return prev;
      return { ...prev, [field]: value };
    });
    // Set dirty state in a separate effect to prevent re-render cascade
    setTimeout(() => setIsDirty(true), 0);
  }, []);

  // Stable tag functions to prevent re-renders
  const addTag = useCallback(() => {
    if (newTag.trim()) {
      setFormData((prev: any) => {
        if (prev.tags.includes(newTag.trim())) return prev;
        return { ...prev, tags: [...prev.tags, newTag.trim()] };
      });
      setNewTag("");
      setTimeout(() => setIsDirty(true), 0);
    }
  }, [newTag]);

  const removeTag = useCallback((tagToRemove: string) => {
    setFormData((prev: any) => ({
      ...prev,
      tags: prev.tags.filter((tag: string) => tag !== tagToRemove)
    }));
    setTimeout(() => setIsDirty(true), 0);
  }, []);

  const handleSave = async () => {
    try {
      if (onSave) {
        // For edit mode, resolve tag names to IDs
        if (formData.tags && formData.tags.length > 0) {
          const tagIds = await createTagsMutation.mutateAsync(formData.tags);
          onSave({ ...formData, tags: tagIds });
        } else {
          onSave(formData);
        }
        return;
      }

      // Validate required fields
      if (!formData.title.trim()) {
        toast({
          title: "Validation Error",
          description: "Post title is required.",
          variant: "destructive",
        });
        return;
      }

      // Create slug from title if not provided
      const slug = formData.title.toLowerCase()
        .replace(/[^a-z0-9]+/g, '-')
        .replace(/(^-|-$)/g, '');

      let postData = {
        ...formData,
        slug,
        status: "draft",
        categoryId: formData.category // Map category to categoryId
      };

      // Remove the old category field
      delete (postData as any).category;

      // Resolve tag names to IDs if tags exist
      if (formData.tags && formData.tags.length > 0) {
        const tagIds = await createTagsMutation.mutateAsync(formData.tags);
        postData = { ...postData, tags: tagIds };
      }

      await createPostMutation.mutateAsync(postData);

      toast({
        title: "Success",
        description: "Post saved as draft successfully.",
      });

      setIsDirty(false);
      // Navigate back to posts list
      setLocation("/posts");
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to save post. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handlePublish = async () => {
    try {
      if (onPublish) {
        // For edit mode, resolve tag names to IDs and map category
        let publishData = { 
          ...formData, 
          status: "published",
          categoryId: formData.category
        };
        delete (publishData as any).category;

        if (formData.tags && formData.tags.length > 0) {
          const tagIds = await createTagsMutation.mutateAsync(formData.tags);
          onPublish({ ...publishData, tags: tagIds });
        } else {
          onPublish(publishData);
        }
        return;
      }

      // Validate required fields
      if (!formData.title.trim()) {
        toast({
          title: "Validation Error",
          description: "Post title is required.",
          variant: "destructive",
        });
        return;
      }

      if (!formData.content.trim()) {
        toast({
          title: "Validation Error",
          description: "Post content is required.",
          variant: "destructive",
        });
        return;
      }

      // Create slug from title if not provided
      const slug = formData.title.toLowerCase()
        .replace(/[^a-z0-9]+/g, '-')
        .replace(/(^-|-$)/g, '');

      let postData = {
        ...formData,
        slug,
        status: "published",
        publishedAt: new Date().toISOString(),
        categoryId: formData.category // Map category to categoryId
      };

      // Remove the old category field
      delete (postData as any).category;

      // Resolve tag names to IDs if tags exist
      if (formData.tags && formData.tags.length > 0) {
        const tagIds = await createTagsMutation.mutateAsync(formData.tags);
        postData = { ...postData, tags: tagIds };
      }

      await createPostMutation.mutateAsync(postData);

      toast({
        title: "Success",
        description: "Post published successfully!",
      });

      setIsDirty(false);
      // Navigate back to posts list
      setLocation("/posts");
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to publish post. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handlePreview = useCallback(() => {
    setPreviewData(formData);
    setIsPreviewOpen(true);
    onPreview(formData);
  }, [formData, onPreview]);

  const ContentTab = useCallback(() => (
    <div className="space-y-6">
      <div className="space-y-2">
        <Label htmlFor="title">Title *</Label>
        <Input
          id="title"
          placeholder="Enter your post title..."
          value={formData.title}
          onChange={(e) => updateField("title", e.target.value)}
          className="text-lg font-medium"
          data-testid="post-title"
        />
      </div>

      <div className="space-y-2">
        <Label htmlFor="content">Content *</Label>
        <RichTextEditor
          ref={editorRef}
          content={formData.content}
          placeholder="Write your post content here..."
          onChange={(content) => updateField("content", content)}
          editorClassName="min-h-[300px] md:min-h-[400px]"
        />
        <p className="text-xs text-muted-foreground">
          {editorRef.current?.getText().length || 0} characters
        </p>
      </div>

      <div className="space-y-2">
        <Label htmlFor="excerpt">Excerpt</Label>
        <Textarea
          id="excerpt"
          placeholder="Brief description of your post..."
          value={formData.excerpt}
          onChange={(e) => updateField("excerpt", e.target.value)}
          className="h-20"
          data-testid="post-excerpt"
        />
        <p className="text-xs text-muted-foreground">
          Optional summary that appears in post previews
        </p>
      </div>

      <div className="flex gap-2">
        <Button variant="outline" size="sm" className="flex-1">
          <ImagePlus className="h-4 w-4 mr-2" />
          Add Media
        </Button>
        <Button variant="outline" size="sm" onClick={handlePreview}>
          <Eye className="h-4 w-4 mr-2" />
          Preview
        </Button>
      </div>
    </div>
  ), [formData.title, formData.content, formData.excerpt, updateField, handlePreview]);

  const MetaTab = useCallback(() => (
    <div className="space-y-6">
      <div className="space-y-4">
        <div className="space-y-2">
          <Label htmlFor="category">Category</Label>
          <Select value={formData.category} onValueChange={(value) => updateField("category", value)}>
            <SelectTrigger data-testid="post-category">
              <SelectValue placeholder="Select category" />
            </SelectTrigger>
            <SelectContent>
              {categories.map((category: any) => (
                <SelectItem key={category.id} value={category.id}>
                  {category.name}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        <div className="space-y-2">
          <Label>Tags</Label>
          <div className="flex gap-2">
            <Input
              placeholder="Add a tag..."
              value={newTag}
              onChange={(e) => setNewTag(e.target.value)}
              onKeyPress={(e) => e.key === "Enter" && (e.preventDefault(), addTag())}
              className="flex-1"
              data-testid="new-tag-input"
            />
            <Button type="button" onClick={addTag} size="sm">
              <Tag className="h-4 w-4" />
            </Button>
          </div>
          {formData.tags.length > 0 && (
            <div className="flex flex-wrap gap-2 mt-2">
              {formData.tags.map((tag: string) => (
                <Badge 
                  key={tag} 
                  variant="secondary" 
                  className="cursor-pointer hover:bg-destructive hover:text-destructive-foreground"
                  onClick={() => removeTag(tag)}
                  data-testid={`tag-${tag}`}
                >
                  {tag} Ã—
                </Badge>
              ))}
            </div>
          )}
        </div>

        <div className="space-y-2">
          <Label htmlFor="publishDate">Publish Date</Label>
          <Input
            id="publishDate"
            type="datetime-local"
            value={formData.publishDate}
            onChange={(e) => updateField("publishDate", e.target.value)}
            data-testid="publish-date"
          />
          <p className="text-xs text-muted-foreground">
            Leave empty to publish immediately
          </p>
        </div>
      </div>

      <Separator />

      <div className="space-y-4">
        <h4 className="font-medium">Post Options</h4>

        <div className="flex items-center justify-between">
          <div className="space-y-0.5">
            <Label>Featured Post</Label>
            <p className="text-sm text-muted-foreground">
              Show this post prominently
            </p>
          </div>
          <Switch
            checked={formData.featured}
            onCheckedChange={(checked) => updateField("featured", checked)}
            data-testid="featured-toggle"
          />
        </div>

        <div className="flex items-center justify-between">
          <div className="space-y-0.5">
            <Label>Allow Comments</Label>
            <p className="text-sm text-muted-foreground">
              Let readers comment on this post
            </p>
          </div>
          <Switch
            checked={formData.allowComments}
            onCheckedChange={(checked) => updateField("allowComments", checked)}
            data-testid="comments-toggle"
          />
        </div>
      </div>
    </div>
  ), [formData.category, formData.tags, formData.publishDate, formData.featured, formData.allowComments, newTag, updateField, addTag, removeTag]);

  const SeoTab = useCallback(() => (
    <div className="space-y-6">
      <div className="space-y-2">
        <Label htmlFor="seoTitle">SEO Title</Label>
        <Input
          id="seoTitle"
          placeholder="SEO optimized title..."
          value={formData.seoTitle}
          onChange={(e) => updateField("seoTitle", e.target.value)}
          data-testid="seo-title"
        />
        <p className="text-xs text-muted-foreground">
          {formData.seoTitle.length}/60 characters recommended
        </p>
      </div>

      <div className="space-y-2">
        <Label htmlFor="seoDescription">Meta Description</Label>
        <Textarea
          id="seoDescription"
          placeholder="Brief description for search engines..."
          value={formData.seoDescription}
          onChange={(e) => updateField("seoDescription", e.target.value)}
          className="h-20"
          data-testid="seo-description"
        />
        <p className="text-xs text-muted-foreground">
          {formData.seoDescription.length}/160 characters recommended
        </p>
      </div>

      <Card>
        <CardHeader>
          <CardTitle className="text-sm">Search Preview</CardTitle>
        </CardHeader>
        <CardContent className="space-y-1">
          <div className="text-lg text-blue-600 font-medium line-clamp-1">
            {formData.seoTitle || formData.title || "Your Post Title"}
          </div>
          <div className="text-sm text-green-700">
            yoursite.com/posts/{formData.title.toLowerCase().replace(/\s+/g, "-") || "post-slug"}
          </div>
          <div className="text-sm text-gray-600 line-clamp-2">
            {formData.seoDescription || formData.excerpt || "Your post description will appear here..."}
          </div>
        </CardContent>
      </Card>
    </div>
  ), [formData.seoTitle, formData.seoDescription, formData.title, formData.excerpt, updateField]);

  return (
    <Page data-testid="new-post-page">
      <PageHeader>
        <div className="px-4 md:px-6">
          <PageToolbar>
            <div className="flex items-center gap-3">
              <Button 
                variant="ghost" 
                size="icon" 
                onClick={() => setLocation("/posts")}
                data-testid="back-button"
                aria-label="Go back to posts"
                className="hover:bg-accent"
              >
                <ArrowLeft className="h-5 w-5" />
              </Button>
              <div className="flex items-center gap-2">
                <PageTitle className="type-heading">
                  {formData.title || (mode === 'edit' ? "Edit Post" : "New Post")}
                </PageTitle>
                {isDirty && (
                  <Badge variant="outline" className="text-xs bg-warning/10 text-warning border-warning/20">
                    Unsaved
                  </Badge>
                )}
              </div>
            </div>
            <PageActions>
              <div className="hidden md:flex items-center gap-2">
                <Button variant="outline" onClick={handlePreview} data-testid="preview-desktop">
                  <Eye className="h-4 w-4 mr-2" />
                  Preview
                </Button>
                <Button 
                  variant="outline" 
                  onClick={handleSave} 
                  disabled={isLoading}
                  data-testid="save-desktop"
                >
                  <Save className="h-4 w-4 mr-2" />
                  {isLoading ? "Saving..." : (mode === 'edit' ? "Save Changes" : "Save Draft")}
                </Button>
                <Button 
                  onClick={handlePublish} 
                  disabled={isLoading}
                  data-testid="publish-desktop"
                >
                  <Globe className="h-4 w-4 mr-2" />
                  {isLoading ? "Publishing..." : "Publish"}
                </Button>
                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button variant="ghost" size="icon" data-testid="more-actions">
                      <MoreHorizontal className="h-4 w-4" />
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent align="end">
                    <DropdownMenuItem>Schedule Post</DropdownMenuItem>
                    <DropdownMenuItem>Save as Template</DropdownMenuItem>
                    <DropdownMenuItem className="text-destructive">Discard Changes</DropdownMenuItem>
                  </DropdownMenuContent>
                </DropdownMenu>
              </div>
            </PageActions>
          </PageToolbar>
        </div>
      </PageHeader>

      <PageBody className="px-4 md:px-6">
        {/* Mobile Tabs */}
        <div className="md:hidden">
          <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-4">
            <TabsList className="grid w-full grid-cols-3">
              <TabsTrigger value="content" className="text-xs">
                <div className="flex flex-col items-center gap-1">
                  <span>Content</span>
                </div>
              </TabsTrigger>
              <TabsTrigger value="meta" className="text-xs">
                <div className="flex flex-col items-center gap-1">
                  <span>Settings</span>
                </div>
              </TabsTrigger>
              <TabsTrigger value="seo" className="text-xs">
                <div className="flex flex-col items-center gap-1">
                  <span>SEO</span>
                </div>
              </TabsTrigger>
            </TabsList>

            <TabsContent value="content" className="space-y-4">
              <ContentTab />
            </TabsContent>

            <TabsContent value="meta" className="space-y-4">
              <MetaTab />
            </TabsContent>

            <TabsContent value="seo" className="space-y-4">
              <SeoTab />
            </TabsContent>
          </Tabs>
        </div>

        {/* Desktop Layout */}
        <div className="hidden md:grid md:grid-cols-3 md:gap-6">
          <div className="md:col-span-2 space-y-6">
            <ContentTab />
          </div>

          <div className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Settings className="h-4 w-4" />
                  Post Settings
                </CardTitle>
              </CardHeader>
              <CardContent>
                <MetaTab />
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Globe className="h-4 w-4" />
                  SEO Settings
                </CardTitle>
              </CardHeader>
              <CardContent>
                <SeoTab />
              </CardContent>
            </Card>
          </div>
        </div>
      </PageBody>

      {/* Mobile Action Bar */}
      <ActionBar show={true}>
        <div className="flex items-center gap-2">
          <Button 
            variant="outline" 
            size="sm" 
            onClick={handleSave} 
            disabled={isLoading}
            data-testid="save-mobile"
          >
            <Save className="h-4 w-4 mr-1" />
            {isLoading ? "Saving..." : (mode === 'edit' ? "Save" : "Save")}
          </Button>
          <Button 
            size="sm" 
            onClick={handlePublish} 
            disabled={isLoading}
            data-testid="publish-mobile"
          >
            <Globe className="h-4 w-4 mr-1" />
            {isLoading ? "Publishing..." : "Publish"}
          </Button>
        </div>
        <Button variant="outline" size="sm" onClick={handlePreview} data-testid="preview-mobile">
          <Eye className="h-4 w-4" />
        </Button>
      </ActionBar>

      {/* Preview Modal */}
      {previewData && (
        <PostPreviewModal
          isOpen={isPreviewOpen}
          onClose={() => setIsPreviewOpen(false)}
          postData={previewData}
        />
      )}
    </Page>
  );
}

