import { useState } from "react";
import { Search, RotateCcw, Trash2, MoreHorizontal, Filter, FileText, Image, Users, Calendar } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Checkbox } from "@/components/ui/checkbox";
import { 
  Page, 
  PageHeader, 
  PageTitle, 
  PageToolbar, 
  PageBody, 
  ActionBar,
  FilterSheet
} from "@/components/ui/page";
import { EmptyStates } from "@/components/ui/empty-state";

interface TrashedItem {
  id: string;
  name: string;
  type: "post" | "page" | "media" | "user" | "category";
  deletedDate: string;
  deletedBy: string;
  originalLocation?: string;
  size?: number;
}

interface TrashPageProps {
  onRestore?: (ids: string[]) => void;
  onPermanentDelete?: (ids: string[]) => void;
  onEmptyTrash?: () => void;
}

// Mock data
const mockTrashedItems: TrashedItem[] = [
  {
    id: "1",
    name: "Old Blog Post About React",
    type: "post",
    deletedDate: "2024-03-15",
    deletedBy: "John Doe",
    originalLocation: "/posts"
  },
  {
    id: "2",
    name: "unused-banner.jpg",
    type: "media",
    deletedDate: "2024-03-14",
    deletedBy: "Jane Smith",
    originalLocation: "/media/images",
    size: 245760
  },
  {
    id: "3",
    name: "Privacy Policy Draft",
    type: "page",
    deletedDate: "2024-03-13",
    deletedBy: "Admin",
    originalLocation: "/pages"
  },
  {
    id: "4",
    name: "Legacy Category",
    type: "category",
    deletedDate: "2024-03-12",
    deletedBy: "Editor",
    originalLocation: "/categories"
  },
  {
    id: "5",
    name: "Former Employee",
    type: "user",
    deletedDate: "2024-03-10",
    deletedBy: "HR Manager",
    originalLocation: "/users"
  }
];

export default function TrashPage({ 
  onRestore = (ids) => console.log("Restore items:", ids),
  onPermanentDelete = (ids) => console.log("Permanently delete:", ids),
  onEmptyTrash = () => console.log("Empty trash")
}: TrashPageProps) {
  const [searchQuery, setSearchQuery] = useState("");
  const [typeFilter, setTypeFilter] = useState<string>("all");
  const [selectedItems, setSelectedItems] = useState<string[]>([]);

  const filteredItems = mockTrashedItems.filter(item => {
    const matchesSearch = item.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         item.deletedBy.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesType = typeFilter === "all" || item.type === typeFilter;
    return matchesSearch && matchesType;
  });

  const handleSelectItem = (itemId: string, checked: boolean) => {
    if (checked) {
      setSelectedItems([...selectedItems, itemId]);
    } else {
      setSelectedItems(selectedItems.filter(id => id !== itemId));
    }
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      setSelectedItems(filteredItems.map(item => item.id));
    } else {
      setSelectedItems([]);
    }
  };

  const formatFileSize = (bytes?: number) => {
    if (!bytes) return "";
    const units = ['B', 'KB', 'MB', 'GB'];
    let size = bytes;
    let unitIndex = 0;

    while (size >= 1024 && unitIndex < units.length - 1) {
      size /= 1024;
      unitIndex++;
    }

    return ` â€¢ ${size.toFixed(1)} ${units[unitIndex]}`;
  };

  const getTypeIcon = (type: TrashedItem["type"]) => {
    const icons = {
      post: FileText,
      page: FileText,
      media: Image,
      user: Users,
      category: Calendar
    };
    return icons[type] || FileText;
  };

  const getTypeBadge = (type: TrashedItem["type"]) => {
    const colors = {
      post: "bg-blue-100 text-blue-800",
      page: "bg-green-100 text-green-800",
      media: "bg-purple-100 text-purple-800",
      user: "bg-orange-100 text-orange-800",
      category: "bg-pink-100 text-pink-800"
    };

    return (
      <Badge variant="outline" className={`capitalize ${colors[type]}`}>
        {type}
      </Badge>
    );
  };

  const getDaysAgo = (dateString: string) => {
    const deletedDate = new Date(dateString);
    const now = new Date();
    const diffTime = Math.abs(now.getTime() - deletedDate.getTime());
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays === 1) return "1 day ago";
    return `${diffDays} days ago`;
  };

  const TrashCard = ({ item }: { item: TrashedItem }) => {
    const Icon = getTypeIcon(item.type);

    return (
      <Card className="hover-elevate" data-testid={`trash-item-${item.id}`}>
        <CardHeader className="pb-2">
          <div className="flex items-start justify-between">
            <div className="flex items-center gap-3 min-w-0 flex-1">
              <Checkbox
                checked={selectedItems.includes(item.id)}
                onCheckedChange={(checked) => handleSelectItem(item.id, !!checked)}
                data-testid={`item-checkbox-${item.id}`}
              />
              <Icon className="h-4 w-4 text-muted-foreground shrink-0" />
              <div className="min-w-0 flex-1">
                <CardTitle className="text-sm line-clamp-1">{item.name}</CardTitle>
              </div>
            </div>
            <div className="flex items-center gap-2">
              {getTypeBadge(item.type)}
              <DropdownMenu>
                <DropdownMenuTrigger asChild>
                  <Button variant="ghost" size="icon" className="shrink-0" data-testid={`trash-menu-${item.id}`}>
                    <MoreHorizontal className="h-4 w-4" />
                  </Button>
                </DropdownMenuTrigger>
                <DropdownMenuContent align="end">
                  <DropdownMenuItem onClick={() => onRestore([item.id])}>
                    <RotateCcw className="h-4 w-4 mr-2" />
                    Restore
                  </DropdownMenuItem>
                  <DropdownMenuItem 
                    onClick={() => onPermanentDelete([item.id])}
                    className="text-destructive focus:text-destructive"
                  >
                    <Trash2 className="h-4 w-4 mr-2" />
                    Delete Forever
                  </DropdownMenuItem>
                </DropdownMenuContent>
              </DropdownMenu>
            </div>
          </div>
        </CardHeader>
        <CardContent className="pt-0">
          <div className="space-y-2 text-sm text-muted-foreground">
            <p>Deleted by {item.deletedBy}</p>
            <p>{getDaysAgo(item.deletedDate)}{formatFileSize(item.size)}</p>
            {item.originalLocation && (
              <p className="text-xs">From: {item.originalLocation}</p>
            )}
          </div>
        </CardContent>
      </Card>
    );
  };

  const FilterControls = () => (
    <div className="space-y-4">
      <div className="space-y-2">
        <label className="text-sm font-medium">Item Type</label>
        <Select value={typeFilter} onValueChange={setTypeFilter}>
          <SelectTrigger>
            <SelectValue />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Types</SelectItem>
            <SelectItem value="post">Posts</SelectItem>
            <SelectItem value="page">Pages</SelectItem>
            <SelectItem value="media">Media</SelectItem>
            <SelectItem value="user">Users</SelectItem>
            <SelectItem value="category">Categories</SelectItem>
          </SelectContent>
        </Select>
      </div>
    </div>
  );

  return (
    <Page data-testid="trash-page">
      <PageHeader>
        <div className="px-4 md:px-6">
          <PageToolbar>
            <PageTitle>
              Trash & Archived
            </PageTitle>
            <div className="hidden md:flex items-center gap-2">
              {mockTrashedItems.length > 0 && (
                <Button 
                  variant="destructive" 
                  onClick={onEmptyTrash}
                  data-testid="empty-trash-desktop"
                >
                  <Trash2 className="h-4 w-4 mr-2" />
                  Empty Trash
                </Button>
              )}
            </div>
          </PageToolbar>

          <div className="mt-4 flex flex-col gap-3 md:flex-row md:items-center md:justify-between">
            <div className="flex items-center gap-2 flex-1 max-w-md">
              <div className="relative flex-1">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
                <Input
                  placeholder="Search deleted items..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-9"
                  data-testid="search-trash"
                />
              </div>
              <FilterSheet title="Filter Items">
                <FilterControls />
              </FilterSheet>
            </div>

            {/* Desktop Filters */}
            <div className="hidden md:flex items-center gap-2">
              <Select value={typeFilter} onValueChange={setTypeFilter}>
                <SelectTrigger className="w-32">
                  <SelectValue placeholder="Type" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Types</SelectItem>
                  <SelectItem value="post">Posts</SelectItem>
                  <SelectItem value="page">Pages</SelectItem>
                  <SelectItem value="media">Media</SelectItem>
                  <SelectItem value="user">Users</SelectItem>
                  <SelectItem value="category">Categories</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
        </div>
      </PageHeader>

      <PageBody className="px-4 md:px-6">
        {filteredItems.length === 0 ? (
          searchQuery || typeFilter !== "all" ? (
            <div className="text-center py-12">
              <h3 className="text-lg font-semibold mb-2">No items found</h3>
              <p className="text-sm text-muted-foreground">
                Try adjusting your search or filter criteria.
              </p>
            </div>
          ) : (
            <EmptyStates.Trash />
          )
        ) : (
          <>
            {/* Bulk Actions Bar */}
            <div className="flex items-center justify-between mb-4">
              <div className="flex items-center gap-2">
                <Checkbox
                  checked={selectedItems.length === filteredItems.length && filteredItems.length > 0}
                  onCheckedChange={handleSelectAll}
                  data-testid="select-all-items"
                />
                <span className="text-sm text-muted-foreground">
                  {selectedItems.length > 0 
                    ? `${selectedItems.length} of ${filteredItems.length} selected`
                    : `${filteredItems.length} items in trash`
                  }
                </span>
              </div>

              {selectedItems.length > 0 && (
                <div className="hidden md:flex items-center gap-2">
                  <Button 
                    variant="outline" 
                    size="sm" 
                    onClick={() => onRestore(selectedItems)}
                    data-testid="restore-selected"
                  >
                    <RotateCcw className="h-4 w-4 mr-2" />
                    Restore Selected
                  </Button>
                  <Button 
                    variant="destructive" 
                    size="sm" 
                    onClick={() => onPermanentDelete(selectedItems)}
                    data-testid="delete-selected"
                  >
                    <Trash2 className="h-4 w-4 mr-2" />
                    Delete Forever
                  </Button>
                </div>
              )}
            </div>

            {/* Items Grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {filteredItems.map(item => (
                <TrashCard key={item.id} item={item} />
              ))}
            </div>
          </>
        )}
      </PageBody>

      {/* Mobile Action Bar for bulk actions */}
      <ActionBar show={selectedItems.length > 0}>
        <span className="text-sm font-medium">
          {selectedItems.length} selected
        </span>
        <div className="flex gap-2">
          <Button variant="outline" size="sm" onClick={() => onRestore(selectedItems)}>
            <RotateCcw className="h-4 w-4 mr-1" />
            Restore
          </Button>
          <Button variant="destructive" size="sm" onClick={() => onPermanentDelete(selectedItems)}>
            <Trash2 className="h-4 w-4 mr-1" />
            Delete
          </Button>
        </div>
      </ActionBar>
    </Page>
  );
}

client/src/components/UploadPage.tsx.content:
import { useState, useRef } from "react";
import { useLocation } from "wouter";
import { Upload, X, FileIcon, Image, Video, FileText, Check, AlertCircle, ArrowLeft } from "lucide-react";
import { useUploadFiles } from "@/hooks/useMedia";
import { useToast } from "@/hooks/use-toast";
import { queryClient } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Label } from "@/components/ui/label";
import { 
  Page, 
  PageHeader, 
  PageTitle, 
  PageToolbar, 
  PageBody
} from "@/components/ui/page";

interface UploadFile {
  id: string;
  file: File;
  progress: number;
  status: "pending" | "uploading" | "completed" | "error";
  error?: string;
}

interface UploadPageProps {
  onUploadComplete?: () => void;
}

export default function UploadPage({ 
  onUploadComplete
}: UploadPageProps) {
  const [uploadFiles, setUploadFiles] = useState<UploadFile[]>([]);
  const [destination, setDestination] = useState("media-library");
  const [isDragOver, setIsDragOver] = useState(false);
  const [isUploading, setIsUploading] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const uploadMutation = useUploadFiles();

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
    const files = Array.from(e.dataTransfer.files);
    addFiles(files);
  };

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(e.target.files || []);
    addFiles(files);
  };

  const addFiles = (files: File[]) => {
    const newUploadFiles = files.map(file => ({
      id: Math.random().toString(36).substr(2, 9),
      file,
      progress: 0,
      status: "pending" as const
    }));

    setUploadFiles(prev => [...prev, ...newUploadFiles]);
  };

  const startUpload = async () => {
    const pendingFiles = uploadFiles.filter(f => f.status === "pending" || f.status === "error");

    if (pendingFiles.length === 0) return;

    setIsUploading(true);

    try {
      // Mark files as uploading
      setUploadFiles(prev => prev.map(f => 
        pendingFiles.includes(f) ? { ...f, status: "uploading", progress: 0 } : f
      ));

      // Upload files using the real API
      await uploadMutation.mutateAsync({
        files: pendingFiles.map(f => f.file),
        alt: "Uploaded file",
        caption: ""
      });

      // Mark files as completed
      setUploadFiles(prev => prev.map(f => 
        pendingFiles.includes(f) ? { ...f, status: "completed", progress: 100 } : f
      ));

      toast({
        title: "Upload successful",
        description: `${pendingFiles.length} file(s) uploaded successfully.`,
      });

      // Invalidate media cache to refresh MediaLibrary
      queryClient.invalidateQueries({ queryKey: ['/api/media'] });

      onUploadComplete?.();

      // Navigate back to media library after 2 seconds
      setTimeout(() => {
        setLocation("/media");
      }, 2000);

    } catch (error: any) {
      // Mark files as error
      setUploadFiles(prev => prev.map(f => 
        pendingFiles.includes(f) ? { 
          ...f, 
          status: "error", 
          error: error.message || "Failed to upload file"
        } : f
      ));

      toast({
        title: "Upload failed",
        description: error.message || "Failed to upload files. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsUploading(false);
    }
  };

  const removeFile = (fileId: string) => {
    setUploadFiles(prev => prev.filter(f => f.id !== fileId));
  };

  const retryUpload = (fileId: string) => {
    setUploadFiles(prev => prev.map(f => 
      f.id === fileId ? { ...f, status: "pending", progress: 0, error: undefined } : f
    ));
  };

  const formatFileSize = (bytes: number) => {
    const units = ['B', 'KB', 'MB', 'GB'];
    let size = bytes;
    let unitIndex = 0;

    while (size >= 1024 && unitIndex < units.length - 1) {
      size /= 1024;
      unitIndex++;
    }

    return `${size.toFixed(1)} ${units[unitIndex]}`;
  };

  const getFileIcon = (type: string) => {
    if (type.startsWith('image/')) return Image;
    if (type.startsWith('video/')) return Video;
    if (type.startsWith('audio/')) return Video;
    return FileText;
  };

  const getStatusIcon = (status: UploadFile["status"]) => {
    switch (status) {
      case "completed":
        return <Check className="h-4 w-4 text-green-500" />;
      case "error":
        return <AlertCircle className="h-4 w-4 text-destructive" />;
      default:
        return null;
    }
  };

  const completedCount = uploadFiles.filter(f => f.status === "completed").length;
  const errorCount = uploadFiles.filter(f => f.status === "error").length;
  const uploadingCount = uploadFiles.filter(f => f.status === "uploading").length;
  const pendingCount = uploadFiles.filter(f => f.status === "pending").length;

  return (
    <Page data-testid="upload-page">
      <PageHeader>
        <div className="px-4 md:px-6">
          <PageToolbar>
            <div className="flex items-center gap-3">
              <Button 
                variant="ghost" 
                size="icon" 
                onClick={() => setLocation("/media")}
                data-testid="back-button"
                aria-label="Go back to media library"
              >
                <ArrowLeft className="h-5 w-5" />
              </Button>
              <PageTitle>
                Upload Media
              </PageTitle>
            </div>
            <div className="flex items-center gap-2">
              <Button 
                variant="outline" 
                onClick={() => setUploadFiles([])}
                disabled={isUploading || uploadFiles.length === 0}
                data-testid="clear-all"
              >
                Clear All
              </Button>
              <Button 
                onClick={startUpload}
                disabled={isUploading || pendingCount === 0}
                data-testid="start-upload"
              >
                <Upload className="h-4 w-4 mr-2" />
                {isUploading ? "Uploading..." : `Upload ${pendingCount + errorCount} File${pendingCount + errorCount !== 1 ? 's' : ''}`}
              </Button>
            </div>
          </PageToolbar>

          <div className="mt-4 max-w-md">
            <Label htmlFor="destination">Upload Destination</Label>
            <Select value={destination} onValueChange={setDestination}>
              <SelectTrigger data-testid="upload-destination">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="media-library">Media Library</SelectItem>
                <SelectItem value="post-images">Post Images</SelectItem>
                <SelectItem value="user-avatars">User Avatars</SelectItem>
                <SelectItem value="site-assets">Site Assets</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </div>
      </PageHeader>

      <PageBody className="px-4 md:px-6">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Upload Zone */}
          <div className="space-y-6">
            <Card
              className={`border-2 border-dashed transition-colors ${
                isDragOver ? "border-primary bg-primary/5" : "border-muted-foreground/25"
              }`}
              onDragOver={handleDragOver}
              onDragLeave={handleDragLeave}
              onDrop={handleDrop}
              data-testid="upload-dropzone"
            >
              <CardContent className="flex flex-col items-center justify-center py-12 text-center">
                <Upload className="h-12 w-12 text-muted-foreground mb-4" />
                <h3 className="text-lg font-semibold mb-2">Drop files here to upload</h3>
                <p className="text-sm text-muted-foreground mb-4">
                  or click to browse your computer
                </p>
                <Button onClick={() => fileInputRef.current?.click()} data-testid="browse-files">
                  Browse Files
                </Button>
                <input
                  ref={fileInputRef}
                  type="file"
                  multiple
                  className="hidden"
                  onChange={handleFileSelect}
                  accept="image/*,video/*,audio/*,.pdf,.doc,.docx,.txt"
                />
                <p className="text-xs text-muted-foreground mt-4">
                  Supports: Images, Videos, Audio, Documents (Max 10MB each)
                </p>
              </CardContent>
            </Card>

            {/* Upload Stats */}
            {uploadFiles.length > 0 && (
              <Card>
                <CardContent className="p-4">
                  <h4 className="font-medium mb-3">Upload Summary</h4>
                  <div className="grid grid-cols-3 gap-4 text-center">
                    <div>
                      <div className="text-2xl font-bold text-green-600">{completedCount}</div>
                      <div className="text-xs text-muted-foreground">Completed</div>
                    </div>
                    <div>
                      <div className="text-2xl font-bold text-blue-600">{uploadingCount}</div>
                      <div className="text-xs text-muted-foreground">Uploading</div>
                    </div>
                    <div>
                      <div className="text-2xl font-bold text-red-600">{errorCount}</div>
                      <div className="text-xs text-muted-foreground">Failed</div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            )}
          </div>

          {/* Upload Queue */}
          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <h3 className="text-lg font-semibold">
                Upload Queue ({uploadFiles.length})
              </h3>
              {uploadFiles.length > 0 && (
                <Button 
                  variant="outline" 
                  size="sm" 
                  onClick={() => setUploadFiles([])}
                  data-testid="clear-queue"
                >
                  Clear All
                </Button>
              )}
            </div>

            <div className="space-y-3 max-h-96 overflow-y-auto">
              {uploadFiles.length === 0 ? (
                <Card>
                  <CardContent className="flex flex-col items-center justify-center py-8 text-center">
                    <FileIcon className="h-8 w-8 text-muted-foreground mb-2" />
                    <p className="text-sm text-muted-foreground">
                      No files in queue. Add files to start uploading.
                    </p>
                  </CardContent>
                </Card>
              ) : (
                uploadFiles.map(uploadFile => {
                  const Icon = getFileIcon(uploadFile.file.type);
                  const statusIcon = getStatusIcon(uploadFile.status);

                  return (
                    <Card key={uploadFile.id} className="overflow-hidden" data-testid={`upload-item-${uploadFile.id}`}>
                      <CardContent className="p-3">
                        <div className="flex items-center gap-3">
                          <div className="flex-shrink-0">
                            <Icon className="h-6 w-6 text-muted-foreground" />
                          </div>

                          <div className="flex-1 min-w-0">
                            <div className="flex items-center justify-between mb-1">
                              <p className="text-sm font-medium line-clamp-1">
                                {uploadFile.file.name}
                              </p>
                              <div className="flex items-center gap-2">
                                {statusIcon}
                                <Button
                                  variant="ghost"
                                  size="icon"
                                  className="h-6 w-6"
                                  onClick={() => removeFile(uploadFile.id)}
                                  data-testid={`remove-file-${uploadFile.id}`}
                                >
                                  <X className="h-3 w-3" />
                                </Button>
                              </div>
                            </div>

                            <div className="flex items-center justify-between text-xs text-muted-foreground mb-2">
                              <span>{formatFileSize(uploadFile.file.size)}</span>
                              <Badge 
                                variant={
                                  uploadFile.status === "completed" ? "default" :
                                  uploadFile.status === "error" ? "destructive" :
                                  uploadFile.status === "uploading" ? "secondary" : "outline"
                                }
                                className="text-xs"
                              >
                                {uploadFile.status}
                              </Badge>
                            </div>

                            {uploadFile.status === "uploading" && (
                              <Progress value={uploadFile.progress} className="h-1" />
                            )}

                            {uploadFile.status === "error" && uploadFile.error && (
                              <div className="flex items-center justify-between">
                                <p className="text-xs text-destructive">{uploadFile.error}</p>
                                <Button
                                  variant="outline"
                                  size="sm"
                                  onClick={() => retryUpload(uploadFile.id)}
                                  data-testid={`retry-upload-${uploadFile.id}`}
                                >
                                  Retry
                                </Button>
                              </div>
                            )}
                          </div>
                        </div>
                      </CardContent>
                    </Card>
                  );
                })
              )}
            </div>
          </div>
        </div>
      </PageBody>
    </Page>
  );
}

