  description?: string;
  action?: {
    label: string;
    onClick: () => void;
    variant?: "default" | "outline" | "secondary";
  };
  className?: string;
  "data-testid"?: string;
}

export function EmptyState({ 
  icon, 
  title, 
  description, 
  action, 
  className,
  "data-testid": testId
}: EmptyStateProps) {
  return (
    <div className={cn(
      "flex flex-col items-center justify-center text-center py-12 px-4",
      className
    )} data-testid={testId || "empty-state"}>
      {icon && (
        <div className="mb-4 p-3 bg-muted rounded-full">
