import {
  Home,
  FileText,
  Plus,
  Tag,
  Layout,
  Image,
  Upload,
  Calendar,
  Users,
  BarChart3,
  Settings,
  Trash2,
} from "lucide-react";
import {
  Sidebar,
  SidebarContent,
  SidebarGroup,
  SidebarGroupContent,
  SidebarGroupLabel,
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
  SidebarRail,
} from "@/components/ui/sidebar";
import { Badge } from "@/components/ui/badge";
import { Link, useLocation } from "wouter";
import { useEffect } from "react";
import { useSidebar } from "@/components/ui/sidebar";
import { cn } from "@/lib/utils";

interface AdminSidebarProps {
  activeItem?: string;
  onNavigate?: () => void;
}

export default function AdminSidebar({ activeItem = "dashboard", onNavigate }: AdminSidebarProps) {
  const [location] = useLocation();
  const { state, isMobile, setOpenMobile } = useSidebar();

  // Debug logging for sidebar navigation
  useEffect(() => {
    if (import.meta.env.DEV) {
      console.log('üìç [SIDEBAR] Active item changed:', {
        activeItem,
        location,
        timestamp: new Date().toISOString()
      });
    }
  }, [activeItem, location]);
  const menuItems = [
    {
      title: "Dashboard",
      url: "/",
      icon: Home,
    },
  ];

  const contentItems = [
    {
      title: "All Posts",
      url: "/posts",
      icon: FileText,
      badge: "124",
    },
    {
      title: "New Post",
      url: "/posts/new",
      icon: Plus,
    },
    {
      title: "Categories / Tags",
      url: "/categories",
      icon: Tag,
    },
  ];

  const layoutItems = [
    {
      title: "Sections Board",
      url: "/layout/sections",
      icon: Layout,
    },
    {
      title: "Hero / Featured",
      url: "/layout/hero",
      icon: Layout,
    },
    {
      title: "Sidebars & Widgets",
      url: "/layout/widgets",
      icon: Layout,
    },
  ];

  const mediaItems = [
    {
      title: "Media Library",
      url: "/media",
      icon: Image,
      badge: "2.1k",
    },
    {
      title: "Upload",
      url: "/media/upload",
      icon: Upload,
    },
  ];

  const systemItems = [
    {
      title: "Scheduling",
      url: "/schedule",
      icon: Calendar,
    },
    {
      title: "Users & Roles",
      url: "/users",
      icon: Users,
    },
    {
      title: "Analytics & Reports",
      url: "/analytics",
      icon: BarChart3,
    },
    {
      title: "Settings",
      url: "/settings",
      icon: Settings,
    },
    {
      title: "Trash / Archived",
      url: "/trash",
      icon: Trash2,
    },
  ];

  const isActiveRoute = (url: string) => {
    if (url === "/" && location === "/") return true;
    if (url !== "/" && location.startsWith(url)) return true;
    return false;
  };

  const renderMenuItems = (items: any[], groupTitle?: string) => (
    <SidebarGroup>
      {groupTitle && <SidebarGroupLabel>{groupTitle}</SidebarGroupLabel>}
      <SidebarGroupContent>
        <SidebarMenu>
          {items.map((item) => (
            <SidebarMenuItem key={item.url}>
              <SidebarMenuButton 
                asChild
                isActive={isActiveRoute(item.url)}
                data-testid={`sidebar-${item.url.replace(/\//g, '-').replace(/^-/, '')}`}
                className={cn(
                  "w-full justify-start transition-all duration-200 hover:scale-[1.01] active:scale-[0.98]",
                  "hover:shadow-sm active:shadow-none"
                )}
              >
                <Link 
                  href={item.url}
                  onClick={() => {
                    if (import.meta.env.DEV) {
                      console.log('üîó [SIDEBAR] Navigation clicked:', {
                        from: location,
                        to: item.url,
                        title: item.title,
                        timestamp: new Date().toISOString()
                      });
                    }
                    // Close mobile sheet when navigating
                    if (isMobile) {
                      setOpenMobile(false);
                    }
                    onNavigate?.();
                  }}
                  className="flex items-center gap-2 w-full transition-colors duration-150 group"
                >
                  <item.icon className="w-4 h-4 transition-transform duration-200 group-hover:scale-110" />
                  {state === "expanded" && (
                    <>
                      <span className="transition-opacity duration-200">{item.title}</span>
                      {item.badge && (
                        <Badge variant="secondary" className="ml-auto transition-all duration-200 hover:scale-105">
                          {item.badge}
                        </Badge>
                      )}
                    </>
                  )}
                </Link>
              </SidebarMenuButton>
            </SidebarMenuItem>
          ))}
        </SidebarMenu>
      </SidebarGroupContent>
    </SidebarGroup>
  );

  return (
    <Sidebar className="transition-all duration-300 ease-in-out">
      <SidebarContent className="space-y-1">
        {renderMenuItems(menuItems)}
        {renderMenuItems(contentItems, "Content")}
        {renderMenuItems(layoutItems, "Layout Manager")}
        {renderMenuItems(mediaItems, "Media Library")}
        {renderMenuItems(systemItems, "System")}
      </SidebarContent>
      <SidebarRail />
    </Sidebar>
  );
}

