  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [newItemData, setNewItemData] = useState({ name: "", description: "" });

  const filteredCategories = mockCategories.filter(category =>
    category.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    category.description?.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const filteredTags = mockTags.filter(tag =>
    tag.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const handleCreateItem = () => {
    if (activeTab === "categories") {
      onCreateCategory({
        name: newItemData.name,
        description: newItemData.description,
        slug: newItemData.name.toLowerCase().replace(/\s+/g, "-")
      });
    } else {
      onCreateTag({
        name: newItemData.name,
        slug: newItemData.name.toLowerCase().replace(/\s+/g, "-")
      });
    }
    setNewItemData({ name: "", description: "" });
    setIsCreateDialogOpen(false);
  };

  const CategoryCard = ({ category }: { category: Category }) => (
    <Card className="hover-elevate" data-testid={`category-card-${category.id}`}>
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between">
          <div className="flex items-center gap-3 min-w-0 flex-1">
            <div 
              className="w-4 h-4 rounded-full shrink-0"
              style={{ backgroundColor: category.color }}
            />
            <div className="min-w-0 flex-1">
              <CardTitle className="text-base line-clamp-1">{category.name}</CardTitle>
              <p className="text-sm text-muted-foreground mt-1">
                /{category.slug}
              </p>
            </div>
          </div>
          <div className="flex items-center gap-2">
            <Badge variant="secondary" className="text-xs">
              {category.postCount} posts
            </Badge>
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button variant="ghost" size="icon" className="shrink-0" data-testid={`category-menu-${category.id}`}>
                  <MoreHorizontal className="h-4 w-4" />
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end">
                <DropdownMenuItem onClick={() => onEditCategory(category.id, category)}>
                  <Edit className="h-4 w-4 mr-2" />
                  Edit
                </DropdownMenuItem>
                <DropdownMenuItem 
                  onClick={() => onDeleteCategory(category.id)}
                  className="text-destructive focus:text-destructive"
                >
                  <Trash2 className="h-4 w-4 mr-2" />
                  Delete
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
          </div>
        </div>
      </CardHeader>
      {category.description && (
        <CardContent className="pt-0">
          <p className="text-sm text-muted-foreground line-clamp-2">
            {category.description}
          </p>
        </CardContent>
      )}
    </Card>
  );

  const TagCard = ({ tag }: { tag: Tag }) => (
    <Card className="hover-elevate" data-testid={`tag-card-${tag.id}`}>
      <CardHeader>
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2 min-w-0 flex-1">
            <Tag className="h-4 w-4 text-muted-foreground shrink-0" />
            <div className="min-w-0 flex-1">
              <CardTitle className="text-base line-clamp-1">{tag.name}</CardTitle>
              <p className="text-sm text-muted-foreground">
                /{tag.slug}
              </p>
            </div>
          </div>
          <div className="flex items-center gap-2">
            <Badge variant="secondary" className="text-xs">
              {tag.postCount}
            </Badge>
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button variant="ghost" size="icon" className="shrink-0" data-testid={`tag-menu-${tag.id}`}>
                  <MoreHorizontal className="h-4 w-4" />
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end">
                <DropdownMenuItem onClick={() => onEditTag(tag.id, tag)}>
                  <Edit className="h-4 w-4 mr-2" />
                  Edit
                </DropdownMenuItem>
                <DropdownMenuItem 
                  onClick={() => onDeleteTag(tag.id)}
                  className="text-destructive focus:text-destructive"
                >
                  <Trash2 className="h-4 w-4 mr-2" />
                  Delete
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
          </div>
        </div>
      </CardHeader>
    </Card>
  );

  const CreateDialog = () => (
    <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
      <DialogContent data-testid="create-dialog">
        <DialogHeader>
          <DialogTitle>
            Create New {activeTab === "categories" ? "Category" : "Tag"}
          </DialogTitle>
        </DialogHeader>
        <div className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="name">Name *</Label>
            <Input
              id="name"
              placeholder={`Enter ${activeTab === "categories" ? "category" : "tag"} name...`}
              value={newItemData.name}
              onChange={(e) => setNewItemData(prev => ({ ...prev, name: e.target.value }))}
              data-testid="item-name"
            />
          </div>
          {activeTab === "categories" && (
            <div className="space-y-2">
              <Label htmlFor="description">Description</Label>
              <Textarea
                id="description"
                placeholder="Enter category description..."
                value={newItemData.description}
                onChange={(e) => setNewItemData(prev => ({ ...prev, description: e.target.value }))}
                className="h-20"
                data-testid="item-description"
              />
            </div>
          )}
          <div className="flex justify-end gap-2">
            <Button variant="outline" onClick={() => setIsCreateDialogOpen(false)}>
              Cancel
            </Button>
            <Button onClick={handleCreateItem} disabled={!newItemData.name.trim()}>
              Create {activeTab === "categories" ? "Category" : "Tag"}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );

  return (
    <Page data-testid="categories-page">
      <PageHeader>
        <div className="px-4 md:px-6">
          <PageToolbar>
            <PageTitle>
              Categories & Tags
            </PageTitle>
            <PageActions>
              <div className="hidden md:flex items-center gap-2">
                <Button onClick={() => setIsCreateDialogOpen(true)} data-testid="create-desktop">
                  <Plus className="h-4 w-4 mr-2" />
                  New {activeTab === "categories" ? "Category" : "Tag"}
                </Button>
              </div>
            </PageActions>
          </PageToolbar>

          <div className="mt-4">
            <div className="relative max-w-md">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
              <Input
                placeholder={`Search ${activeTab}...`}
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-9"
                data-testid="search-input"
              />
            </div>
          </div>
        </div>
      </PageHeader>

      <PageBody className="px-4 md:px-6">
        <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-6">
          <TabsList className="grid w-full grid-cols-2 max-w-md">
            <TabsTrigger value="categories" className="flex items-center gap-2">
              <FolderPlus className="h-4 w-4" />
              Categories ({mockCategories.length})
            </TabsTrigger>
            <TabsTrigger value="tags" className="flex items-center gap-2">
              <Tag className="h-4 w-4" />
              Tags ({mockTags.length})
            </TabsTrigger>
          </TabsList>

          <TabsContent value="categories" className="space-y-4">
            {filteredCategories.length === 0 ? (
              searchQuery ? (
                <div className="text-center py-12">
                  <h3 className="text-lg font-semibold mb-2">No categories found</h3>
                  <p className="text-sm text-muted-foreground">
                    Try adjusting your search terms.
                  </p>
                </div>
              ) : (
                <EmptyState
                  icon={<FolderPlus className="h-8 w-8 text-muted-foreground" />}
                  title="No categories yet"
                  description="Create your first category to organize your posts."
                  action={{
                    label: "Create Category",
                    onClick: () => setIsCreateDialogOpen(true)
                  }}
                />
              )
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {filteredCategories.map(category => (
                  <CategoryCard key={category.id} category={category} />
                ))}
              </div>
            )}
          </TabsContent>

          <TabsContent value="tags" className="space-y-4">
            {filteredTags.length === 0 ? (
              searchQuery ? (
                <div className="text-center py-12">
                  <h3 className="text-lg font-semibold mb-2">No tags found</h3>
                  <p className="text-sm text-muted-foreground">
                    Try adjusting your search terms.
                  </p>
                </div>
              ) : (
                <EmptyState
                  icon={<Tag className="h-8 w-8 text-muted-foreground" />}
                  title="No tags yet"
                  description="Create your first tag to help categorize your content."
                  action={{
                    label: "Create Tag",
                    onClick: () => setIsCreateDialogOpen(true)
                  }}
                />
              )
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                {filteredTags.map(tag => (
                  <TagCard key={tag.id} tag={tag} />
                ))}
              </div>
            )}
          </TabsContent>
        </Tabs>
      </PageBody>

      {/* Mobile FAB */}
      <FAB 
        onClick={() => setIsCreateDialogOpen(true)}
        aria-label={`Create new ${activeTab === "categories" ? "category" : "tag"}`}
        data-testid="create-fab"
      >
        New {activeTab === "categories" ? "Category" : "Tag"}
      </FAB>

      <CreateDialog />
    </Page>
  );
}

client/src/components/MediaLibraryPage.t.content:
import { useState } from "react";
import { Search, Grid, List, Upload, Filter, MoreHorizontal, Download, Eye, Trash2, Folder, Image, Video, FileText } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { Card, CardContent } from "@/components/ui/card";
import { Checkbox } from "@/components/ui/checkbox";
import { 
  Page, 
  PageHeader, 
  PageTitle, 
  PageToolbar, 
  PageBody, 
  PageActions,
  ActionBar,
  FAB,
  FilterSheet
} from "@/components/ui/page";
import { EmptyState } from "@/components/ui/empty-state";
import { LoadingGrid } from "@/components/ui/loading";

interface MediaFile {
  id: string;
  name: string;
  type: "image" | "video" | "audio" | "document";
  url: string;
  size: number;
  uploadDate: string;
  dimensions?: { width: number; height: number };
  duration?: number;
  mimeType: string;
}

interface MediaLibraryPageProps {
  onUpload?: () => void;
  onSelect?: (file: MediaFile) => void;
  onDelete?: (ids: string[]) => void;
  onDownload?: (ids: string[]) => void;
}

// Mock data
const mockFiles: MediaFile[] = [
  {
    id: "1",
    name: "hero-banner.jpg",
    type: "image",
    url: "/images/hero-banner.jpg",
    size: 245760,
    uploadDate: "2024-03-15",
    dimensions: { width: 1920, height: 1080 },
    mimeType: "image/jpeg"
  },
  {
    id: "2", 
    name: "product-demo.mp4",
    type: "video",
    url: "/videos/product-demo.mp4",
    size: 15728640,
    uploadDate: "2024-03-14",
    dimensions: { width: 1280, height: 720 },
    duration: 120,
    mimeType: "video/mp4"
  },
  {
    id: "3",
    name: "user-guide.pdf",
    type: "document",
    url: "/documents/user-guide.pdf",
    size: 1048576,
    uploadDate: "2024-03-13",
    mimeType: "application/pdf"
  },
  {
    id: "4",
    name: "logo-variants.png",
    type: "image",
    url: "/images/logo-variants.png",
    size: 89600,
    uploadDate: "2024-03-12",
    dimensions: { width: 800, height: 600 },
    mimeType: "image/png"
  },
  {
    id: "5",
    name: "background-music.mp3",
    type: "audio",
    url: "/audio/background-music.mp3",
    size: 5242880,
    uploadDate: "2024-03-11",
    duration: 180,
    mimeType: "audio/mpeg"
  }
];

export default function MediaLibraryPage({ 
  onUpload = () => console.log("Upload files"),
  onSelect = (file) => console.log("Select file:", file),
  onDelete = (ids) => console.log("Delete files:", ids),
  onDownload = (ids) => console.log("Download files:", ids)
}: MediaLibraryPageProps) {
  const [searchQuery, setSearchQuery] = useState("");
  const [typeFilter, setTypeFilter] = useState<string>("all");
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid");
  const [selectedFiles, setSelectedFiles] = useState<string[]>([]);
  const [isLoading, setIsLoading] = useState(false);

  // Filter files based on search and filters
  const filteredFiles = mockFiles.filter(file => {
    const matchesSearch = file.name.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesType = typeFilter === "all" || file.type === typeFilter;
    return matchesSearch && matchesType;
  });

  const handleSelectFile = (fileId: string, checked: boolean) => {
    if (checked) {
      setSelectedFiles([...selectedFiles, fileId]);
    } else {
      setSelectedFiles(selectedFiles.filter(id => id !== fileId));
    }
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      setSelectedFiles(filteredFiles.map(file => file.id));
    } else {
      setSelectedFiles([]);
    }
  };

  const formatFileSize = (bytes: number) => {
    const units = ['B', 'KB', 'MB', 'GB'];
    let size = bytes;
    let unitIndex = 0;

    while (size >= 1024 && unitIndex < units.length - 1) {
      size /= 1024;
      unitIndex++;
    }

    return `${size.toFixed(1)} ${units[unitIndex]}`;
  };

  const getFileIcon = (type: MediaFile["type"]) => {
    const icons = {
      image: Image,
      video: Video,
      audio: Video, // Using video icon for audio
      document: FileText
    };
    return icons[type] || FileText;
  };

  const getFileTypeColor = (type: MediaFile["type"]) => {
    const colors = {
      image: "bg-blue-500",
      video: "bg-purple-500", 
      audio: "bg-green-500",
      document: "bg-orange-500"
    };
    return colors[type] || "bg-gray-500";
  };

  const MediaGridItem = ({ file }: { file: MediaFile }) => {
    const Icon = getFileIcon(file.type);

    return (
      <Card className="hover-elevate group" data-testid={`media-item-${file.id}`}>
        <CardContent className="p-3">
          <div className="flex items-start justify-between mb-2">
            <Checkbox
              checked={selectedFiles.includes(file.id)}
              onCheckedChange={(checked) => handleSelectFile(file.id, !!checked)}
              data-testid={`file-checkbox-${file.id}`}
            />
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button variant="ghost" size="icon" className="shrink-0 opacity-0 group-hover:opacity-100" data-testid={`file-menu-${file.id}`}>
                  <MoreHorizontal className="h-4 w-4" />
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end">
                <DropdownMenuItem onClick={() => onSelect(file)}>
                  <Eye className="h-4 w-4 mr-2" />
                  View
                </DropdownMenuItem>
                <DropdownMenuItem onClick={() => onDownload([file.id])}>
                  <Download className="h-4 w-4 mr-2" />
                  Download
                </DropdownMenuItem>
                <DropdownMenuItem 
                  onClick={() => onDelete([file.id])}
                  className="text-destructive focus:text-destructive"
                >
                  <Trash2 className="h-4 w-4 mr-2" />
                  Delete
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
          </div>

          <div className="aspect-square bg-muted rounded-lg flex items-center justify-center mb-3 relative overflow-hidden">
            {file.type === "image" ? (
              <img 
                src={file.url} 
                alt={file.name}
                className="w-full h-full object-cover"
                onError={(e) => {
                  const target = e.target as HTMLImageElement;
                  target.style.display = "none";
                  target.nextElementSibling?.classList.remove("hidden");
                }}
              />
            ) : null}
            <div className={`absolute inset-0 flex items-center justify-center ${file.type === "image" ? "hidden" : ""}`}>
              <div className={`p-3 rounded-full ${getFileTypeColor(file.type)}`}>
                <Icon className="h-6 w-6 text-white" />
              </div>
            </div>
            {file.duration && (
              <div className="absolute bottom-2 right-2 bg-black/75 text-white text-xs px-1.5 py-0.5 rounded">
                {Math.floor(file.duration / 60)}:{(file.duration % 60).toString().padStart(2, "0")}
              </div>
            )}
          </div>

          <div className="space-y-1">
            <p className="text-sm font-medium line-clamp-1" title={file.name}>
              {file.name}
            </p>
            <div className="flex items-center justify-between text-xs text-muted-foreground">
              <span>{formatFileSize(file.size)}</span>
              {file.dimensions && (
                <span>{file.dimensions.width}√ó{file.dimensions.height}</span>
              )}
            </div>
            <p className="text-xs text-muted-foreground">
              {new Date(file.uploadDate).toLocaleDateString()}
            </p>
          </div>
        </CardContent>
      </Card>
    );
  };

  const MediaListItem = ({ file }: { file: MediaFile }) => {
    const Icon = getFileIcon(file.type);

    return (
      <div className="flex items-center gap-3 p-3 border rounded-lg hover-elevate group" data-testid={`media-item-${file.id}`}>
        <Checkbox
          checked={selectedFiles.includes(file.id)}
          onCheckedChange={(checked) => handleSelectFile(file.id, !!checked)}
          data-testid={`file-checkbox-${file.id}`}
        />

        <div className={`p-2 rounded ${getFileTypeColor(file.type)}`}>
          <Icon className="h-4 w-4 text-white" />
        </div>

        <div className="flex-1 min-w-0">
          <p className="font-medium line-clamp-1">{file.name}</p>
          <div className="flex items-center gap-2 text-sm text-muted-foreground">
            <span>{formatFileSize(file.size)}</span>
            {file.dimensions && (
              <>
                <span>‚Ä¢</span>
                <span>{file.dimensions.width}√ó{file.dimensions.height}</span>
              </>
            )}
            {file.duration && (
              <>
                <span>‚Ä¢</span>
                <span>{Math.floor(file.duration / 60)}:{(file.duration % 60).toString().padStart(2, "0")}</span>
              </>
            )}
            <span>‚Ä¢</span>
            <span>{new Date(file.uploadDate).toLocaleDateString()}</span>
          </div>
        </div>

        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <Button variant="ghost" size="icon" className="shrink-0 opacity-0 group-hover:opacity-100" data-testid={`file-menu-${file.id}`}>
              <MoreHorizontal className="h-4 w-4" />
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent align="end">
            <DropdownMenuItem onClick={() => onSelect(file)}>
              <Eye className="h-4 w-4 mr-2" />
              View
            </DropdownMenuItem>
            <DropdownMenuItem onClick={() => onDownload([file.id])}>
              <Download className="h-4 w-4 mr-2" />
              Download
            </DropdownMenuItem>
            <DropdownMenuItem 
              onClick={() => onDelete([file.id])}
              className="text-destructive focus:text-destructive"
            >
              <Trash2 className="h-4 w-4 mr-2" />
              Delete
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>
      </div>
    );
  };

  const FilterControls = () => (
    <div className="space-y-4">
      <div className="space-y-2">
        <label className="text-sm font-medium">File Type</label>
        <Select value={typeFilter} onValueChange={setTypeFilter}>
          <SelectTrigger>
            <SelectValue />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Types</SelectItem>
            <SelectItem value="image">Images</SelectItem>
            <SelectItem value="video">Videos</SelectItem>
            <SelectItem value="audio">Audio</SelectItem>
            <SelectItem value="document">Documents</SelectItem>
          </SelectContent>
        </Select>
      </div>
    </div>
  );

  return (
    <Page data-testid="media-library-page">
      <PageHeader>
        <div className="px-4 md:px-6">
          <PageToolbar>
            <PageTitle badge="2.1k">
              Media Library
            </PageTitle>
            <PageActions>
              <div className="hidden md:flex items-center gap-2">
                <Button
                  variant={viewMode === "grid" ? "default" : "outline"}
                  size="icon"
                  onClick={() => setViewMode("grid")}
                  data-testid="view-grid"
                >
                  <Grid className="h-4 w-4" />
                </Button>
                <Button
                  variant={viewMode === "list" ? "default" : "outline"}
                  size="icon"
                  onClick={() => setViewMode("list")}
                  data-testid="view-list"
                >
                  <List className="h-4 w-4" />
                </Button>
                <Button onClick={onUpload} data-testid="upload-desktop">
                  <Upload className="h-4 w-4 mr-2" />
                  Upload
                </Button>
              </div>
            </PageActions>
          </PageToolbar>

          <div className="mt-4 flex flex-col gap-3 md:flex-row md:items-center md:justify-between">
            <div className="flex items-center gap-2 flex-1 max-w-md">
              <div className="relative flex-1">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
                <Input
                  placeholder="Search files..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-9"
                  data-testid="search-files"
                />
              </div>
              <FilterSheet title="Filter Files">
                <FilterControls />
              </FilterSheet>
            </div>

            {/* Desktop Filters */}
            <div className="hidden md:flex items-center gap-2">
              <Select value={typeFilter} onValueChange={setTypeFilter}>
                <SelectTrigger className="w-36">
                  <SelectValue placeholder="File Type" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Types</SelectItem>
                  <SelectItem value="image">Images</SelectItem>
                  <SelectItem value="video">Videos</SelectItem>
                  <SelectItem value="audio">Audio</SelectItem>
                  <SelectItem value="document">Documents</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
        </div>
      </PageHeader>

      <PageBody className="px-4 md:px-6">
        {isLoading ? (
          <LoadingGrid items={12} columns={viewMode === "grid" ? 4 : 1} />
        ) : filteredFiles.length === 0 ? (
          searchQuery || typeFilter !== "all" ? (
            <div className="text-center py-12">
              <h3 className="text-lg font-semibold mb-2">No files found</h3>
              <p className="text-sm text-muted-foreground">
                Try adjusting your search or filter criteria.
              </p>
            </div>
          ) : (
            <EmptyState
              icon={<Folder className="h-8 w-8 text-muted-foreground" />}
              title="No media files"
              description="Upload images, videos, and documents to your media library."
              action={{
                label: "Upload Files",
                onClick: onUpload
              }}
            />
          )
        ) : (
          <>
            {/* Selection Bar */}
            {selectedFiles.length > 0 && (
              <div className="bg-muted/50 border rounded-lg p-3 mb-4">
                <div className="flex items-center justify-between">
                  <span className="text-sm font-medium">
                    {selectedFiles.length} file{selectedFiles.length !== 1 ? 's' : ''} selected
                  </span>
                  <div className="flex items-center gap-2">
                    <Button variant="outline" size="sm" onClick={() => onDownload(selectedFiles)}>
                      <Download className="h-4 w-4 mr-1" />
                      Download
                    </Button>
                    <Button variant="outline" size="sm" className="text-destructive" onClick={() => onDelete(selectedFiles)}>
                      <Trash2 className="h-4 w-4 mr-1" />
                      Delete
                    </Button>
                  </div>
                </div>
              </div>
            )}

            {/* Files Content */}
            {viewMode === "grid" ? (
              <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4">
                {filteredFiles.map(file => (
                  <MediaGridItem key={file.id} file={file} />
                ))}
              </div>
            ) : (
              <div className="space-y-2">
                {filteredFiles.map(file => (
                  <MediaListItem key={file.id} file={file} />
                ))}
              </div>
            )}
          </>
        )}
      </PageBody>

      {/* Mobile FAB */}
      <FAB 
        onClick={onUpload}
        aria-label="Upload files"
        data-testid="upload-fab"
      >
        Upload
      </FAB>

      {/* Mobile Action Bar for bulk actions */}
      <ActionBar show={selectedFiles.length > 0}>
        <span className="text-sm font-medium">
          {selectedFiles.length} selected
        </span>
        <div className="flex gap-2">
          <Button variant="outline" size="sm" onClick={() => onDownload(selectedFiles)}>
            <Download className="h-4 w-4" />
          </Button>
          <Button variant="destructive" size="sm" onClick={() => onDelete(selectedFiles)}>
            <Trash2 className="h-4 w-4" />
          </Button>
        </div>
      </ActionBar>
    </Page>
  );
}

client/src/components/SchedulingPage.tsx.content:
import { useState } from "react";
import { Calendar, Clock, Plus, Filter, MoreHorizontal, Edit, Trash2, AlertCircle, CheckCircle } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { 
  Page, 
  PageHeader, 
  PageTitle, 
  PageToolbar, 
  PageBody, 
  PageActions,
  FAB,
  FilterSheet
} from "@/components/ui/page";
import { EmptyStates } from "@/components/ui/empty-state";

interface ScheduledPost {
  id: string;
  title: string;
  type: "post" | "page" | "social";
  scheduledDate: string;
  status: "scheduled" | "published" | "failed" | "cancelled";
  author: string;
  platform?: string;
}

interface SchedulingPageProps {
  onCreateSchedule?: () => void;
  onEditSchedule?: (id: string) => void;
  onDeleteSchedule?: (id: string) => void;
  onPublishNow?: (id: string) => void;
}

// Mock data
const mockScheduledPosts: ScheduledPost[] = [
  {
    id: "1",
    title: "Getting Started with React Development",
    type: "post",
    scheduledDate: "2024-03-20T10:00:00",
    status: "scheduled",
    author: "John Doe"
  },
  {
    id: "2",
    title: "New Product Launch Announcement",
    type: "social",
    scheduledDate: "2024-03-19T14:30:00",
    status: "published",
    author: "Marketing Team",
    platform: "Twitter"
  },
  {
    id: "3",
    title: "Weekly Newsletter Content",
    type: "post",
    scheduledDate: "2024-03-21T09:00:00",
    status: "failed",
    author: "Jane Smith"
  },
  {
    id: "4",
    title: "About Us Page Update",
    type: "page",
    scheduledDate: "2024-03-22T16:00:00",
    status: "scheduled",
    author: "Admin"
  }
];

export default function SchedulingPage({ 
  onCreateSchedule = () => console.log("Create schedule"),
  onEditSchedule = (id) => console.log("Edit schedule:", id),
  onDeleteSchedule = (id) => console.log("Delete schedule:", id),
  onPublishNow = (id) => console.log("Publish now:", id)
}: SchedulingPageProps) {
  const [searchQuery, setSearchQuery] = useState("");
  const [statusFilter, setStatusFilter] = useState<string>("all");
  const [typeFilter, setTypeFilter] = useState<string>("all");

  const filteredPosts = mockScheduledPosts.filter(post => {
    const matchesSearch = post.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         post.author.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesStatus = statusFilter === "all" || post.status === statusFilter;
    const matchesType = typeFilter === "all" || post.type === typeFilter;
    return matchesSearch && matchesStatus && matchesType;
  });

  const getStatusBadge = (status: ScheduledPost["status"]) => {
    const variants = {
      scheduled: { variant: "secondary" as const, icon: Clock },
      published: { variant: "default" as const, icon: CheckCircle },
      failed: { variant: "destructive" as const, icon: AlertCircle },
      cancelled: { variant: "outline" as const, icon: AlertCircle }
    };

    const config = variants[status];
    const Icon = config.icon;

    return (
      <Badge variant={config.variant} className="capitalize flex items-center gap-1">
        <Icon className="h-3 w-3" />
        {status}
      </Badge>
    );
  };

  const getTypeBadge = (type: ScheduledPost["type"]) => {
    const colors = {
      post: "bg-blue-100 text-blue-800",
      page: "bg-green-100 text-green-800", 
      social: "bg-purple-100 text-purple-800"
    };

    return (
      <Badge variant="outline" className={`capitalize ${colors[type]}`}>
        {type}
      </Badge>
    );
  };

  const formatDateTime = (dateString: string) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffMs = date.getTime() - now.getTime();
    const diffDays = Math.ceil(diffMs / (1000 * 60 * 60 * 24));

    const timeStr = date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    const dateStr = date.toLocaleDateString();

    let relative = "";
    if (diffDays === 0) relative = "Today";
    else if (diffDays === 1) relative = "Tomorrow";
    else if (diffDays === -1) relative = "Yesterday";
    else if (diffDays > 1) relative = `In ${diffDays} days`;
    else if (diffDays < -1) relative = `${Math.abs(diffDays)} days ago`;

    return { timeStr, dateStr, relative };
  };

  const ScheduleCard = ({ post }: { post: ScheduledPost }) => {
    const { timeStr, dateStr, relative } = formatDateTime(post.scheduledDate);

    return (
      <Card className="hover-elevate" data-testid={`schedule-card-${post.id}`}>
        <CardHeader className="pb-3">
          <div className="flex items-start justify-between">
            <div className="min-w-0 flex-1">
              <CardTitle className="text-base line-clamp-2 mb-2">{post.title}</CardTitle>
              <div className="flex items-center gap-2 flex-wrap">
                {getStatusBadge(post.status)}
                {getTypeBadge(post.type)}
                {post.platform && (
                  <Badge variant="outline" className="text-xs">
                    {post.platform}
                  </Badge>
                )}
              </div>
            </div>
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button variant="ghost" size="icon" className="shrink-0" data-testid={`schedule-menu-${post.id}`}>
                  <MoreHorizontal className="h-4 w-4" />
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end">
                <DropdownMenuItem onClick={() => onEditSchedule(post.id)}>
                  <Edit className="h-4 w-4 mr-2" />
                  Edit
                </DropdownMenuItem>
                {post.status === "scheduled" && (
                  <DropdownMenuItem onClick={() => onPublishNow(post.id)}>
                    <CheckCircle className="h-4 w-4 mr-2" />
                    Publish Now
                  </DropdownMenuItem>
                )}
                <DropdownMenuItem 
                  onClick={() => onDeleteSchedule(post.id)}
                  className="text-destructive focus:text-destructive"
                >
                  <Trash2 className="h-4 w-4 mr-2" />
                  Delete
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
          </div>
        </CardHeader>
        <CardContent className="pt-0">
          <div className="flex items-center justify-between text-sm">
            <div className="text-muted-foreground">
              By {post.author}
            </div>
            <div className="text-right">
              <div className="font-medium">{relative}</div>
              <div className="text-xs text-muted-foreground">
                {dateStr} at {timeStr}
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    );
  };

  const FilterControls = () => (
    <div className="space-y-4">
      <div className="space-y-2">
        <label className="text-sm font-medium">Status</label>
        <Select value={statusFilter} onValueChange={setStatusFilter}>
          <SelectTrigger>
            <SelectValue />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Status</SelectItem>
            <SelectItem value="scheduled">Scheduled</SelectItem>
            <SelectItem value="published">Published</SelectItem>
            <SelectItem value="failed">Failed</SelectItem>
            <SelectItem value="cancelled">Cancelled</SelectItem>
          </SelectContent>
        </Select>
      </div>

      <div className="space-y-2">
        <label className="text-sm font-medium">Type</label>
        <Select value={typeFilter} onValueChange={setTypeFilter}>
          <SelectTrigger>
            <SelectValue />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Types</SelectItem>
            <SelectItem value="post">Posts</SelectItem>
            <SelectItem value="page">Pages</SelectItem>
            <SelectItem value="social">Social Media</SelectItem>
          </SelectContent>
        </Select>
      </div>
    </div>
  );

  const scheduledCount = mockScheduledPosts.filter(p => p.status === "scheduled").length;
  const failedCount = mockScheduledPosts.filter(p => p.status === "failed").length;

  return (
    <Page data-testid="scheduling-page">
      <PageHeader>
        <div className="px-4 md:px-6">
          <PageToolbar>
            <PageTitle badge={scheduledCount.toString()}>
              Scheduling
            </PageTitle>
            <PageActions>
              <div className="hidden md:flex items-center gap-2">
                <Button onClick={onCreateSchedule} data-testid="create-schedule-desktop">
                  <Plus className="h-4 w-4 mr-2" />
                  Schedule Content
                </Button>
              </div>
            </PageActions>
          </PageToolbar>

          <div className="mt-4 flex flex-col gap-3 md:flex-row md:items-center md:justify-between">
            <div className="flex items-center gap-2 flex-1 max-w-md">
              <div className="relative flex-1">
                <Calendar className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
                <Input
                  placeholder="Search scheduled content..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-9"
                  data-testid="search-scheduled"
                />
              </div>
              <FilterSheet title="Filter Schedule">
                <FilterControls />
              </FilterSheet>
            </div>

            {/* Desktop Filters */}
            <div className="hidden md:flex items-center gap-2">
              <Select value={statusFilter} onValueChange={setStatusFilter}>
                <SelectTrigger className="w-32">
                  <SelectValue placeholder="Status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Status</SelectItem>
                  <SelectItem value="scheduled">Scheduled</SelectItem>
                  <SelectItem value="published">Published</SelectItem>
                  <SelectItem value="failed">Failed</SelectItem>
                  <SelectItem value="cancelled">Cancelled</SelectItem>
                </SelectContent>
              </Select>

              <Select value={typeFilter} onValueChange={setTypeFilter}>
                <SelectTrigger className="w-32">
                  <SelectValue placeholder="Type" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Types</SelectItem>
                  <SelectItem value="post">Posts</SelectItem>
                  <SelectItem value="page">Pages</SelectItem>
                  <SelectItem value="social">Social</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
        </div>
      </PageHeader>

      <PageBody className="px-4 md:px-6">
        {/* Quick Stats */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
          <Card>
            <CardContent className="p-4 text-center">
              <div className="text-2xl font-bold text-blue-600">{scheduledCount}</div>
              <div className="text-sm text-muted-foreground">Scheduled</div>
            </CardContent>
          </Card>
          <Card>
            <CardContent className="p-4 text-center">
              <div className="text-2xl font-bold text-green-600">
                {mockScheduledPosts.filter(p => p.status === "published").length}
              </div>
              <div className="text-sm text-muted-foreground">Published</div>
            </CardContent>
          </Card>
          <Card>
            <CardContent className="p-4 text-center">
              <div className="text-2xl font-bold text-red-600">{failedCount}</div>
              <div className="text-sm text-muted-foreground">Failed</div>
            </CardContent>
          </Card>
          <Card>
            <CardContent className="p-4 text-center">
              <div className="text-2xl font-bold text-gray-600">{mockScheduledPosts.length}</div>
              <div className="text-sm text-muted-foreground">Total</div>
            </CardContent>
          </Card>
        </div>

        {filteredPosts.length === 0 ? (
          searchQuery || statusFilter !== "all" || typeFilter !== "all" ? (
            <div className="text-center py-12">
              <h3 className="text-lg font-semibold mb-2">No scheduled content found</h3>
              <p className="text-sm text-muted-foreground">
                Try adjusting your search or filter criteria.
              </p>
            </div>
          ) : (
            <EmptyStates.Schedule onCreateSchedule={onCreateSchedule} />
          )
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {filteredPosts.map(post => (
              <ScheduleCard key={post.id} post={post} />
            ))}
          </div>
        )}
      </PageBody>

      {/* Mobile FAB */}
      <FAB 
        onClick={onCreateSchedule}
        aria-label="Schedule new content"
        data-testid="schedule-fab"
      >
        Schedule
      </FAB>
    </Page>
  );
}

client/src/components/TrashPage.tsx.content:
import { useState } from "react";
import { Search, RotateCcw, Trash2, MoreHorizontal, Filter, FileText, Image, Users, Calendar } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Checkbox } from "@/components/ui/checkbox";
import { 
  Page, 
  PageHeader, 
  PageTitle, 
  PageToolbar, 
  PageBody, 
  ActionBar,
  FilterSheet
} from "@/components/ui/page";
import { EmptyStates } from "@/components/ui/empty-state";

interface TrashedItem {
  id: string;
  name: string;
  type: "post" | "page" | "media" | "user" | "category";
  deletedDate: string;
  deletedBy: string;
  originalLocation?: string;
  size?: number;
}

interface TrashPageProps {
  onRestore?: (ids: string[]) => void;
  onPermanentDelete?: (ids: string[]) => void;
  onEmptyTrash?: () => void;
}

// Mock data
const mockTrashedItems: TrashedItem[] = [
  {
    id: "1",
    name: "Old Blog Post About React",
    type: "post",
    deletedDate: "2024-03-15",
    deletedBy: "John Doe",
    originalLocation: "/posts"
  },
  {
    id: "2",
    name: "unused-banner.jpg",
    type: "media",
    deletedDate: "2024-03-14",
    deletedBy: "Jane Smith",
    originalLocation: "/media/images",
    size: 245760
  },
  {
    id: "3",
    name: "Privacy Policy Draft",
    type: "page",
    deletedDate: "2024-03-13",
    deletedBy: "Admin",
    originalLocation: "/pages"
  },
  {
    id: "4",
    name: "Legacy Category",
    type: "category",
    deletedDate: "2024-03-12",
    deletedBy: "Editor",
    originalLocation: "/categories"
  },
  {
    id: "5",
    name: "Former Employee",
    type: "user",
    deletedDate: "2024-03-10",
    deletedBy: "HR Manager",
    originalLocation: "/users"
  }
];

export default function TrashPage({ 
  onRestore = (ids) => console.log("Restore items:", ids),
  onPermanentDelete = (ids) => console.log("Permanently delete:", ids),
  onEmptyTrash = () => console.log("Empty trash")
}: TrashPageProps) {
  const [searchQuery, setSearchQuery] = useState("");
  const [typeFilter, setTypeFilter] = useState<string>("all");
  const [selectedItems, setSelectedItems] = useState<string[]>([]);

  const filteredItems = mockTrashedItems.filter(item => {
    const matchesSearch = item.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         item.deletedBy.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesType = typeFilter === "all" || item.type === typeFilter;
    return matchesSearch && matchesType;
  });

  const handleSelectItem = (itemId: string, checked: boolean) => {
    if (checked) {
      setSelectedItems([...selectedItems, itemId]);
    } else {
      setSelectedItems(selectedItems.filter(id => id !== itemId));
    }
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      setSelectedItems(filteredItems.map(item => item.id));
    } else {
      setSelectedItems([]);
    }
  };

  const formatFileSize = (bytes?: number) => {
    if (!bytes) return "";
    const units = ['B', 'KB', 'MB', 'GB'];
    let size = bytes;
    let unitIndex = 0;

    while (size >= 1024 && unitIndex < units.length - 1) {
      size /= 1024;
      unitIndex++;
    }

    return ` ‚Ä¢ ${size.toFixed(1)} ${units[unitIndex]}`;
  };

  const getTypeIcon = (type: TrashedItem["type"]) => {
    const icons = {
      post: FileText,
      page: FileText,
      media: Image,
      user: Users,
      category: Calendar
    };
    return icons[type] || FileText;
  };

  const getTypeBadge = (type: TrashedItem["type"]) => {
    const colors = {
      post: "bg-blue-100 text-blue-800",
      page: "bg-green-100 text-green-800",
      media: "bg-purple-100 text-purple-800",
      user: "bg-orange-100 text-orange-800",
      category: "bg-pink-100 text-pink-800"
    };

    return (
      <Badge variant="outline" className={`capitalize ${colors[type]}`}>
        {type}
      </Badge>
    );
  };

  const getDaysAgo = (dateString: string) => {
    const deletedDate = new Date(dateString);
    const now = new Date();
    const diffTime = Math.abs(now.getTime() - deletedDate.getTime());
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays === 1) return "1 day ago";
    return `${diffDays} days ago`;
  };

  const TrashCard = ({ item }: { item: TrashedItem }) => {
    const Icon = getTypeIcon(item.type);

    return (
      <Card className="hover-elevate" data-testid={`trash-item-${item.id}`}>
        <CardHeader className="pb-2">
          <div className="flex items-start justify-between">
            <div className="flex items-center gap-3 min-w-0 flex-1">
              <Checkbox
                checked={selectedItems.includes(item.id)}
                onCheckedChange={(checked) => handleSelectItem(item.id, !!checked)}
                data-testid={`item-checkbox-${item.id}`}
              />
              <Icon className="h-4 w-4 text-muted-foreground shrink-0" />
              <div className="min-w-0 flex-1">
                <CardTitle className="text-sm line-clamp-1">{item.name}</CardTitle>
              </div>
            </div>
            <div className="flex items-center gap-2">
              {getTypeBadge(item.type)}
              <DropdownMenu>
                <DropdownMenuTrigger asChild>
                  <Button variant="ghost" size="icon" className="shrink-0" data-testid={`trash-menu-${item.id}`}>
                    <MoreHorizontal className="h-4 w-4" />
                  </Button>
                </DropdownMenuTrigger>
                <DropdownMenuContent align="end">
                  <DropdownMenuItem onClick={() => onRestore([item.id])}>
                    <RotateCcw className="h-4 w-4 mr-2" />
                    Restore
                  </DropdownMenuItem>
                  <DropdownMenuItem 
                    onClick={() => onPermanentDelete([item.id])}
                    className="text-destructive focus:text-destructive"
                  >
                    <Trash2 className="h-4 w-4 mr-2" />
                    Delete Forever
                  </DropdownMenuItem>
                </DropdownMenuContent>
              </DropdownMenu>
            </div>
          </div>
        </CardHeader>
        <CardContent className="pt-0">
          <div className="space-y-2 text-sm text-muted-foreground">
            <p>Deleted by {item.deletedBy}</p>
            <p>{getDaysAgo(item.deletedDate)}{formatFileSize(item.size)}</p>
            {item.originalLocation && (
              <p className="text-xs">From: {item.originalLocation}</p>
            )}
          </div>
        </CardContent>
      </Card>
    );
  };

  const FilterControls = () => (
    <div className="space-y-4">
      <div className="space-y-2">
        <label className="text-sm font-medium">Item Type</label>
        <Select value={typeFilter} onValueChange={setTypeFilter}>
          <SelectTrigger>
            <SelectValue />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Types</SelectItem>
            <SelectItem value="post">Posts</SelectItem>
            <SelectItem value="page">Pages</SelectItem>
            <SelectItem value="media">Media</SelectItem>
            <SelectItem value="user">Users</SelectItem>
            <SelectItem value="category">Categories</SelectItem>
          </SelectContent>
        </Select>
      </div>
    </div>
  );

  return (
    <Page data-testid="trash-page">
      <PageHeader>
        <div className="px-4 md:px-6">
          <PageToolbar>
            <PageTitle>
              Trash & Archived
            </PageTitle>
            <div className="hidden md:flex items-center gap-2">
              {mockTrashedItems.length > 0 && (
                <Button 
                  variant="destructive" 
                  onClick={onEmptyTrash}
                  data-testid="empty-trash-desktop"
                >
                  <Trash2 className="h-4 w-4 mr-2" />
                  Empty Trash
                </Button>
              )}
            </div>
          </PageToolbar>

          <div className="mt-4 flex flex-col gap-3 md:flex-row md:items-center md:justify-between">
            <div className="flex items-center gap-2 flex-1 max-w-md">
              <div className="relative flex-1">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
                <Input
                  placeholder="Search deleted items..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-9"
                  data-testid="search-trash"
                />
              </div>
              <FilterSheet title="Filter Items">
                <FilterControls />
              </FilterSheet>
            </div>

            {/* Desktop Filters */}
            <div className="hidden md:flex items-center gap-2">
              <Select value={typeFilter} onValueChange={setTypeFilter}>
                <SelectTrigger className="w-32">
                  <SelectValue placeholder="Type" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Types</SelectItem>
                  <SelectItem value="post">Posts</SelectItem>
                  <SelectItem value="page">Pages</SelectItem>
                  <SelectItem value="media">Media</SelectItem>
                  <SelectItem value="user">Users</SelectItem>
                  <SelectItem value="category">Categories</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
        </div>
      </PageHeader>

      <PageBody className="px-4 md:px-6">
        {filteredItems.length === 0 ? (
          searchQuery || typeFilter !== "all" ? (
            <div className="text-center py-12">
              <h3 className="text-lg font-semibold mb-2">No items found</h3>
              <p className="text-sm text-muted-foreground">
                Try adjusting your search or filter criteria.
              </p>
            </div>
          ) : (
            <EmptyStates.Trash />
          )
        ) : (
          <>
            {/* Bulk Actions Bar */}
            <div className="flex items-center justify-between mb-4">
              <div className="flex items-center gap-2">
                <Checkbox
                  checked={selectedItems.length === filteredItems.length && filteredItems.length > 0}
                  onCheckedChange={handleSelectAll}
                  data-testid="select-all-items"
                />
                <span className="text-sm text-muted-foreground">
                  {selectedItems.length > 0 
                    ? `${selectedItems.length} of ${filteredItems.length} selected`
                    : `${filteredItems.length} items in trash`
                  }
                </span>
              </div>

              {selectedItems.length > 0 && (
                <div className="hidden md:flex items-center gap-2">
                  <Button 
                    variant="outline" 
                    size="sm" 
                    onClick={() => onRestore(selectedItems)}
                    data-testid="restore-selected"
                  >
                    <RotateCcw className="h-4 w-4 mr-2" />
                    Restore Selected
                  </Button>
                  <Button 
                    variant="destructive" 
                    size="sm" 
                    onClick={() => onPermanentDelete(selectedItems)}
                    data-testid="delete-selected"
                  >
                    <Trash2 className="h-4 w-4 mr-2" />
                    Delete Forever
                  </Button>
                </div>
              )}
            </div>

            {/* Items Grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {filteredItems.map(item => (
                <TrashCard key={item.id} item={item} />
              ))}
            </div>
          </>
        )}
      </PageBody>

      {/* Mobile Action Bar for bulk actions */}
      <ActionBar show={selectedItems.length > 0}>
        <span className="text-sm font-medium">
          {selectedItems.length} selected
        </span>
        <div className="flex gap-2">
          <Button variant="outline" size="sm" onClick={() => onRestore(selectedItems)}>
            <RotateCcw className="h-4 w-4 mr-1" />
            Restore
          </Button>
          <Button variant="destructive" size="sm" onClick={() => onPermanentDelete(selectedItems)}>
            <Trash2 className="h-4 w-4 mr-1" />
            Delete
          </Button>
        </div>
      </ActionBar>
    </Page>
  );
}

