      type: "hero",
      title: "Welcome Hero",
      content: "Main landing page hero with call-to-action",
      status: "active",
      order: 0
    },
    {
      id: "section-2", 
      type: "text",
      title: "About Section",
      content: "Company overview and mission statement",
      status: "active",
      order: 1
    },
    {
      id: "section-3",
      type: "image", 
      title: "Feature Gallery",
      content: "Product screenshots and images",
      status: "draft",
      order: 2
    }
  ];

  const handleDragStart = (e: React.DragEvent, sectionId: string) => {
    setDraggedItem(sectionId);
    e.dataTransfer.effectAllowed = "move";
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = "move";
  };

  const handleDrop = (e: React.DragEvent, targetId: string) => {
    e.preventDefault();
    if (draggedItem && draggedItem !== targetId) {
      console.log(`Move section ${draggedItem} to position of ${targetId}`);
      // TODO: Implement section reordering
    }
    setDraggedItem(null);
  };

  const getStatusColor = (status: string) => {
    return status === "active" ? "bg-green-500" : "bg-yellow-500";
  };

  const getSectionIcon = (type: string) => {
    const section = sectionTypes.find(s => s.id === type);
    return section?.icon || Layout;
  };

  return (
    <div className="p-4 md:p-6 space-y-4 md:space-y-6">
      {/* Header */}
      <div className="flex items-start justify-between gap-4">
        <div>
          <h1 className="text-2xl md:text-3xl font-semibold">Layout Manager</h1>
          <p className="text-muted-foreground text-sm md:text-base">Drag and drop to arrange your page sections</p>
        </div>
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <Button data-testid="button-add-section" size="sm" className="shrink-0">
              <Plus className="w-4 h-4 sm:mr-2" />
              <span className="hidden sm:inline">Add Section</span>
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent align="end" className="w-56">
            {sectionTypes.map((type) => {
              const IconComponent = type.icon;
              return (
                <DropdownMenuItem
                  key={type.id}
                  onClick={() => {
                    onAddSection?.(type.id);
                    console.log(`Add section: ${type.id}`);
                  }}
                  data-testid={`menu-add-${type.id}`}
                >
                  <IconComponent className="w-4 h-4 mr-2" />
                  <div>
                    <p className="font-medium">{type.name}</p>
                    <p className="text-xs text-muted-foreground">{type.description}</p>
                  </div>
                </DropdownMenuItem>
              );
            })}
          </DropdownMenuContent>
        </DropdownMenu>
      </div>

      {/* Section Templates */}
      <Card>
        <CardHeader>
          <CardTitle>Section Templates</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-3 md:gap-4">
            {sectionTypes.map((type) => {
              const IconComponent = type.icon;
              return (
                <Card 
                  key={type.id} 
                  className="hover-elevate cursor-pointer"
                  onClick={() => {
                    onAddSection?.(type.id);
                    console.log(`Add section template: ${type.id}`);
                  }}
                  data-testid={`template-${type.id}`}
                >
                  <CardContent className="p-3 md:p-4 text-center">
                    <IconComponent className="w-8 h-8 mx-auto mb-2 text-primary" />
                    <h4 className="font-medium mb-1">{type.name}</h4>
                    <p className="text-xs text-muted-foreground">{type.description}</p>
                  </CardContent>
                </Card>
              );
            })}
          </div>
        </CardContent>
      </Card>

      {/* Current Layout Sections */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Layout className="w-5 h-5" />
            Page Sections
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-3">
            {currentSections.map((section, index) => {
              const SectionIcon = getSectionIcon(section.type);
              return (
                <Card
                  key={section.id}
                  className={`hover-elevate cursor-move ${draggedItem === section.id ? 'opacity-50' : ''}`}
                  draggable
                  onDragStart={(e) => handleDragStart(e, section.id)}
                  onDragOver={handleDragOver}
                  onDrop={(e) => handleDrop(e, section.id)}
                  data-testid={`section-${section.id}`}
                >
                  <CardContent className="p-3 md:p-4">
                    <div className="flex items-center gap-4">
                      <GripVertical className="w-5 h-5 text-muted-foreground cursor-grab" />

                      <div className="flex items-center gap-3 flex-1">
                        <SectionIcon className="w-5 h-5 text-primary" />

                        <div className="flex-1">
                          <div className="flex items-center gap-2 mb-1">
                            <h4 className="font-medium">{section.title}</h4>
                            <Badge 
                              variant="secondary" 
                              className={`w-2 h-2 p-0 rounded-full ${getStatusColor(section.status)}`}
                            />
                            <span className="text-xs text-muted-foreground capitalize">
                              {section.status}
                            </span>
                          </div>
                          <p className="text-sm text-muted-foreground">{section.content}</p>
                        </div>
                      </div>

                      <div className="flex items-center gap-1">
                        <Button
                          variant="ghost"
                          size="icon"
                          className="w-8 h-8"
                          onClick={() => console.log(`Preview section: ${section.id}`)}
                          data-testid={`button-preview-${section.id}`}
                        >
                          <Eye className="w-4 h-4" />
                        </Button>

                        <Button
                          variant="ghost"
                          size="icon"
                          className="w-8 h-8"
                          onClick={() => {
                            onEditSection?.(section.id);
                            console.log(`Edit section: ${section.id}`);
                          }}
                          data-testid={`button-edit-${section.id}`}
                        >
                          <Edit3 className="w-4 h-4" />
                        </Button>

                        <Button
                          variant="ghost"
                          size="icon" 
                          className="w-8 h-8"
                          onClick={() => console.log(`Duplicate section: ${section.id}`)}
                          data-testid={`button-duplicate-${section.id}`}
                        >
                          <Copy className="w-4 h-4" />
                        </Button>

                        <Button
                          variant="ghost"
                          size="icon"
                          className="w-8 h-8 text-destructive hover:text-destructive"
                          onClick={() => console.log(`Delete section: ${section.id}`)}
                          data-testid={`button-delete-${section.id}`}
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              );
            })}
          </div>

          {currentSections.length === 0 && (
            <div className="text-center py-8">
              <Layout className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
              <h3 className="text-lg font-medium mb-2">No sections yet</h3>
              <p className="text-muted-foreground mb-4">
                Start building your page by adding sections above
              </p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/MediaLibrary.tsx.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { 
  Search, 
  Upload, 
  Grid3x3, 
  List, 
  Filter,
  Image as ImageIcon,
  Video,
  FileText,
  Download,
  Trash2,
  MoreHorizontal
} from "lucide-react";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";

interface MediaLibraryProps {
  onUpload?: () => void;
  onSelectFile?: (file: any) => void;
}

export default function MediaLibrary({ onUpload, onSelectFile }: MediaLibraryProps) {
  const [searchQuery, setSearchQuery] = useState("");
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid");
  const [typeFilter, setTypeFilter] = useState("all");

  // TODO: Remove mock data
  const mediaFiles = [
    {
      id: "1",
      name: "hero-image.jpg",
      type: "image",
      size: "2.4 MB",
      uploadDate: "2024-01-15",
      dimensions: "1920x1080",
      thumbnail: "https://images.unsplash.com/photo-1498050108023-c5249f4df085?w=400&h=300&fit=crop",
      used: true
    },
    {
      id: "2",
      name: "presentation.pdf",
      type: "document",
      size: "5.1 MB", 
      uploadDate: "2024-01-14",
      dimensions: null,
      thumbnail: null,
      used: false
    },
    {
      id: "3",
      name: "demo-video.mp4",
      type: "video",
      size: "45.2 MB",
      uploadDate: "2024-01-12",
      dimensions: "1920x1080",
      thumbnail: "https://images.unsplash.com/photo-1551434678-e076c223a692?w=400&h=300&fit=crop",
      used: true
    },
    {
      id: "4",
      name: "profile-photo.png", 
      type: "image",
      size: "856 KB",
      uploadDate: "2024-01-10",
      dimensions: "512x512",
      thumbnail: "https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=400&h=300&fit=crop",
      used: false
    },
    {
      id: "5",
      name: "screenshot-1.png",
      type: "image", 
      size: "1.2 MB",
      uploadDate: "2024-01-08",
      dimensions: "1440x900",
      thumbnail: "https://images.unsplash.com/photo-1517077304055-6e89abbf09b0?w=400&h=300&fit=crop",
      used: true
    }
  ];

  const getFileIcon = (type: string) => {
    switch (type) {
      case "image": return ImageIcon;
      case "video": return Video;
      case "document": return FileText;
      default: return FileText;
    }
  };

  const getFileTypeColor = (type: string) => {
    switch (type) {
      case "image": return "text-green-500";
      case "video": return "text-purple-500";
      case "document": return "text-blue-500";
      default: return "text-gray-500";
    }
  };

  const filteredFiles = mediaFiles.filter(file => {
    const matchesSearch = file.name.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesType = typeFilter === "all" || file.type === typeFilter;
    return matchesSearch && matchesType;
  });

  const GridView = () => (
    <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 xl:grid-cols-6 gap-3 md:gap-4">
      {filteredFiles.map((file) => {
        const FileIcon = getFileIcon(file.type);
        return (
          <Card 
            key={file.id} 
            className="hover-elevate cursor-pointer"
            onClick={() => {
              onSelectFile?.(file);
              console.log("Selected file:", file.name);
            }}
            data-testid={`file-card-${file.id}`}
          >
            <CardContent className="p-2 md:p-3">
              <div className="aspect-square rounded mb-3 bg-muted flex items-center justify-center overflow-hidden">
                {file.thumbnail ? (
                  <img 
                    src={file.thumbnail} 
                    alt={file.name}
                    className="w-full h-full object-cover"
                  />
                ) : (
                  <FileIcon className={`w-8 h-8 ${getFileTypeColor(file.type)}`} />
                )}
              </div>

              <div className="space-y-1">
                <p className="text-sm font-medium truncate" title={file.name}>
                  {file.name}
                </p>
                <div className="flex items-center justify-between text-xs text-muted-foreground">
                  <span>{file.size}</span>
                  {file.used && (
                    <Badge variant="secondary" className="text-xs px-1">In use</Badge>
                  )}
                </div>
              </div>

              <DropdownMenu>
                <DropdownMenuTrigger asChild onClick={(e) => e.stopPropagation()}>
                  <Button 
                    variant="ghost" 
                    size="icon" 
                    className="absolute top-2 right-2 w-6 h-6 opacity-0 group-hover:opacity-100"
                    data-testid={`file-actions-${file.id}`}
                  >
                    <MoreHorizontal className="w-3 h-3" />
                  </Button>
                </DropdownMenuTrigger>
                <DropdownMenuContent align="end">
                  <DropdownMenuItem data-testid={`menu-download-${file.id}`}>
                    <Download className="w-4 h-4 mr-2" />
                    Download
                  </DropdownMenuItem>
                  <DropdownMenuItem className="text-destructive" data-testid={`menu-delete-${file.id}`}>
                    <Trash2 className="w-4 h-4 mr-2" />
                    Delete
                  </DropdownMenuItem>
                </DropdownMenuContent>
              </DropdownMenu>
            </CardContent>
          </Card>
        );
      })}
    </div>
  );

  const ListView = () => (
    <div className="space-y-2">
      {filteredFiles.map((file) => {
        const FileIcon = getFileIcon(file.type);
        return (
          <Card 
            key={file.id} 
            className="hover-elevate cursor-pointer"
            onClick={() => {
              onSelectFile?.(file);
              console.log("Selected file:", file.name);
            }}
            data-testid={`file-row-${file.id}`}
          >
            <CardContent className="p-3 md:p-4">
              <div className="flex items-center gap-4">
                <div className="w-12 h-12 rounded bg-muted flex items-center justify-center flex-shrink-0 overflow-hidden">
                  {file.thumbnail ? (
                    <img 
                      src={file.thumbnail} 
                      alt={file.name}
                      className="w-full h-full object-cover"
                    />
                  ) : (
                    <FileIcon className={`w-6 h-6 ${getFileTypeColor(file.type)}`} />
                  )}
                </div>

                <div className="flex-1 min-w-0">
                  <p className="font-medium truncate">{file.name}</p>
                  <div className="flex items-center gap-4 text-sm text-muted-foreground">
                    <span>{file.size}</span>
                    {file.dimensions && <span>{file.dimensions}</span>}
                    <span>{new Date(file.uploadDate).toLocaleDateString()}</span>
                  </div>
                </div>

                <div className="flex items-center gap-2">
                  {file.used && (
                    <Badge variant="secondary">In use</Badge>
                  )}

                  <DropdownMenu>
                    <DropdownMenuTrigger asChild onClick={(e) => e.stopPropagation()}>
                      <Button variant="ghost" size="icon" data-testid={`file-actions-list-${file.id}`}>
                        <MoreHorizontal className="w-4 h-4" />
                      </Button>
                    </DropdownMenuTrigger>
                    <DropdownMenuContent align="end">
                      <DropdownMenuItem>
                        <Download className="w-4 h-4 mr-2" />
                        Download
                      </DropdownMenuItem>
                      <DropdownMenuItem className="text-destructive">
                        <Trash2 className="w-4 h-4 mr-2" />
                        Delete
                      </DropdownMenuItem>
                    </DropdownMenuContent>
                  </DropdownMenu>
                </div>
              </div>
            </CardContent>
          </Card>
        );
      })}
    </div>
  );

  return (
    <div className="p-4 md:p-6 space-y-4 md:space-y-6">
      {/* Header */}
      <div className="flex items-start justify-between gap-4">
        <div>
          <h1 className="text-2xl md:text-3xl font-semibold">Media Library</h1>
          <p className="text-muted-foreground text-sm md:text-base">Manage your images, videos, and documents</p>
        </div>
        <Button onClick={onUpload} data-testid="button-upload-media" size="sm" className="shrink-0">
          <Upload className="w-4 h-4 sm:mr-2" />
          <span className="hidden sm:inline">Upload Files</span>
        </Button>
      </div>

      {/* Filters and Controls */}
      <Card>
        <CardContent className="p-3 md:p-4">
          <div className="flex flex-col gap-4">
            <div className="flex flex-col sm:flex-row gap-3 sm:gap-4">
              <div className="relative flex-1">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-muted-foreground" />
                <Input
                  placeholder="Search files..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-10"
                  data-testid="input-media-search"
                />
              </div>

              <Select value={typeFilter} onValueChange={setTypeFilter}>
                <SelectTrigger className="w-full sm:w-[140px]" data-testid="select-type-filter">
                  <Filter className="w-4 h-4 mr-2" />
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Types</SelectItem>
                  <SelectItem value="image">Images</SelectItem>
                  <SelectItem value="video">Videos</SelectItem>
                  <SelectItem value="document">Documents</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div className="flex gap-2 justify-center sm:justify-end">
              <Button
                variant={viewMode === "grid" ? "default" : "outline"}
                size="icon"
                onClick={() => setViewMode("grid")}
                data-testid="button-grid-view"
              >
                <Grid3x3 className="w-4 h-4" />
              </Button>
              <Button
                variant={viewMode === "list" ? "default" : "outline"}
                size="icon"
                onClick={() => setViewMode("list")}
                data-testid="button-list-view"
              >
                <List className="w-4 h-4" />
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Media Content */}
      <div>
        {filteredFiles.length > 0 ? (
          viewMode === "grid" ? <GridView /> : <ListView />
        ) : (
          <Card>
            <CardContent className="text-center py-8 md:py-12">
              <ImageIcon className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
              <h3 className="text-lg font-medium mb-2">No files found</h3>
              <p className="text-muted-foreground mb-4">
                {searchQuery || typeFilter !== "all" 
                  ? "Try adjusting your search or filters"
                  : "Upload your first file to get started"}
              </p>
              {!searchQuery && typeFilter === "all" && (
                <Button onClick={onUpload} data-testid="button-upload-first">
                  <Upload className="w-4 h-4 mr-2" />
                  Upload Files
                </Button>
              )}
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

client/src/components/MobileNavigation.t.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Sheet, SheetContent, SheetTrigger, SheetHeader, SheetTitle } from "@/components/ui/sheet";
import { SidebarProvider } from "@/components/ui/sidebar";
import { Badge } from "@/components/ui/badge";
import { Menu, X } from "lucide-react";
import { useLocation } from "wouter";
import AdminSidebar from "./AdminSidebar";

interface MobileNavigationProps {
  currentPage?: string;
}

export default function MobileNavigation({ currentPage = "dashboard" }: MobileNavigationProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [location] = useLocation();

  // Close mobile menu when route changes
  useEffect(() => {
    if (isOpen) {
      if (process.env.NODE_ENV === 'development') {
        console.log('📱 [MOBILE NAV] Route changed, closing mobile menu:', {
          newLocation: location,
          timestamp: new Date().toISOString()
        });
      }
      setIsOpen(false);
    }
  }, [location]); // Removed isOpen from dependencies to prevent immediate closing

  // Debug logging for mobile menu state
  useEffect(() => {
    if (process.env.NODE_ENV === 'development') {
      console.log('📱 [MOBILE NAV] Menu state changed:', {
        isOpen,
        currentPage,
        location,
        timestamp: new Date().toISOString()
      });
    }
  }, [isOpen, currentPage, location]);

  return (
    <Sheet open={isOpen} onOpenChange={setIsOpen}>
      <SheetTrigger asChild>
        <Button 
          variant="ghost" 
          size="icon" 
          className="lg:hidden"
          data-testid="button-mobile-menu"
        >
          <Menu className="w-5 h-5" />
        </Button>
      </SheetTrigger>
      <SheetContent side="left" className="w-80 p-0">
        <SheetHeader className="p-4 border-b">
          <SheetTitle className="text-left font-bold text-foreground">
            Penkora
          </SheetTitle>
        </SheetHeader>
        <div className="overflow-y-auto h-full">
          <SidebarProvider style={{
            "--sidebar-width": "16rem",
            "--sidebar-width-icon": "3rem",
          } as React.CSSProperties}>
            <AdminSidebar 
              activeItem={currentPage}
            />
          </SidebarProvider>
        </div>
      </SheetContent>
    </Sheet>
  );
}

